import sortedcontainers
class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        l, res = sortedcontainers.SortedList(), []
        for i in range(1, len(nums)-1):
            if nums[i-1]<nums[i]>nums[i+1]: l.add(i)

        for a, b, c in queries:
            if a==1:
                p1 = l.bisect(b)
                p2 = l.bisect_left(c)
                res.append(max(0, p2-p1))
            else:
                nums[b] = c
                for a in {b-1, b, b+1} - {-1, 0, len(nums)-1, len(nums)}:
                    if nums[a-1]<nums[a]>nums[a+1]:
                        if a not in l: l.add(a)
                    else: l.discard(a)
        return res
