import numpy as np
class Solution:
    def countOfPeaks(self, a: List[int], queries: List[List[int]]) -> List[int]:
        n = len(a)
        def f(i):
            if i == 0 or i == n - 1: return 0
            return 1 if a[i] > a[i - 1] and a[i] > a[i + 1] else 0
        b = np.array([f(i) for i in range(n)], np.int32)
        res = []
        for q in queries:
            if q[0] == 2:
                i, x = q[1], q[2]
                a[i] = x
                b[i] = f(i)
                if i > 0: b[i - 1] = f(i - 1)
                if i + 1 < n: b[i + 1] = f(i + 1)
            else:
                l, r = q[1], q[2]
                res.append(int(b[l+1:r].sum()))
        return res
