class Solution {
    public List<Integer> countOfPeaks(int[] nums, int[][] queries) {
        int n = nums.length;
        SegmentTree segmentTree = new SegmentTree(nums);
        List<Integer> result = new ArrayList<>();
        for (int[] query : queries) {
            if (query[0] == 1) {
                int li = query[1];
                int ri = query[2];
                result.add(segmentTree.countPeaks(li, ri));
            } else if (query[0] == 2) {
                int index = query[1];
                int val = query[2];
                segmentTree.update(index, val);
            }
        }
        return result;
    }

    class SegmentTree {
        private int[] nums;
        private int[] peaks;

        public SegmentTree(int[] nums) {
            this.nums = nums;
            this.peaks = new int[nums.length * 4];
            build(0, 0, nums.length - 1);
        }

        private void build(int node, int start, int end) {
            if (start == end) {
                peaks[node] = isPeak(start) ? 1 : 0;
            } else {
                int mid = (start + end) / 2;
                int leftChild = 2 * node + 1;
                int rightChild = 2 * node + 2;
                build(leftChild, start, mid);
                build(rightChild, mid + 1, end);
                peaks[node] = peaks[leftChild] + peaks[rightChild];
            }
        }

        public void update(int index, int val) {
            nums[index] = val;
            update(0, 0, nums.length - 1, index);
            if (index > 0) {
                update(0, 0, nums.length - 1, index - 1);
            }
            if (index < nums.length - 1) {
                update(0, 0, nums.length - 1, index + 1);
            }
        }

        private void update(int node, int start, int end, int index) {
            if (start == end) {
                peaks[node] = isPeak(start) ? 1 : 0;
            } else {
                int mid = (start + end) / 2;
                int leftChild = 2 * node + 1;
                int rightChild = 2 * node + 2;
                if (index <= mid) {
                    update(leftChild, start, mid, index);
                } else {
                    update(rightChild, mid + 1, end, index);
                }
                peaks[node] = peaks[leftChild] + peaks[rightChild];
            }
        }

        public int countPeaks(int li, int ri) {
            if (li + 1 >= ri) {
                return 0;
            }
            return query(0, 0, nums.length - 1, li + 1, ri - 1);
        }

        private int query(int node, int start, int end, int L, int R) {
            if (L > end || R < start) {
                return 0;
            }
            if (L <= start && end <= R) {
                return peaks[node];
            }
            int mid = (start + end) / 2;
            int leftChild = 2 * node + 1;
            int rightChild = 2 * node + 2;
            int leftResult = query(leftChild, start, mid, L, R);
            int rightResult = query(rightChild, mid + 1, end, L, R);
            return leftResult + rightResult;
        }

        private boolean isPeak(int index) {
            if (index <= 0 || index >= nums.length - 1) {
                return false;
            }
            return nums[index] > nums[index - 1] && nums[index] > nums[index + 1];
        }
    }
}
