from sortedcontainers import SortedList

class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        n = len(nums)
        s = SortedList()
        for i in range(1, n - 1):
            if nums[i - 1] < nums[i] and nums[i] > nums[i + 1]:
                s.add(i)
        ans = []
        for z, l, r in queries:
            if z == 1:
                if l + 1 < r:
                    ans.append(s.bisect_right(r - 1) - s.bisect_left(l + 1))
                else:
                    ans.append(0)
            else:
                nums[l] = r
                for x in [l - 1, l, l + 1]:
                    if 0 < x < n - 1:
                        if nums[x - 1] < nums[x] and nums[x] > nums[x + 1]:
                            if x not in s:
                                s.add(x)
                        else:
                            if x in s:
                                s.remove(x)
        return ans
