class SegmentTree {
private:
    vector<int> tree;
    vector<bool> isPeak;
    int n;

    bool isPeakElement(const vector<int>& nums, int i) {
        if (i <= 0 || i >= n - 1) return false;
        return nums[i] > nums[i - 1] && nums[i] > nums[i + 1];
    }

    void build(vector<int>& nums, int node, int start, int end) {
        if (start == end) {
            isPeak[start] = isPeakElement(nums, start);
            tree[node] = isPeak[start] ? 1 : 0;
        } else {
            int mid = (start + end) / 2;
            build(nums, 2 * node + 1, start, mid);
            build(nums, 2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2];
        }
    }

    void update(vector<int>& nums, int node, int start, int end, int idx) {
        if (start == end) {
            isPeak[start] = isPeakElement(nums, start);
            tree[node] = isPeak[start] ? 1 : 0;
        } else {
            int mid = (start + end) / 2;
            if (start <= idx && idx <= mid) {
                update(nums, 2 * node + 1, start, mid, idx);
            } else {
                update(nums, 2 * node + 2, mid + 1, end, idx);
            }
            tree[node] = tree[2 * node + 1] + tree[2 * node + 2];
        }
    }

    int query(int node, int start, int end, int L, int R) {
        if (R < start || end < L) {
            return 0;
        }
        if (L <= start && end <= R) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int left_query = query(2 * node + 1, start, mid, L, R);
        int right_query = query(2 * node + 2, mid + 1, end, L, R);
        return left_query + right_query;
    }

public:
    SegmentTree(vector<int>& nums) {
        n = nums.size();
        tree.resize(4 * n, 0);
        isPeak.resize(n, false);
        build(nums, 0, 0, n - 1);
    }

    void update(vector<int>& nums, int idx) {
        for (int i = max(1, idx - 1); i <= min(n - 2, idx + 1); ++i) {
            update(nums, 0, 0, n - 1, i);
        }
    }

    int query(int L, int R) {
        return query(0, 0, n - 1, L + 1, R - 1); // Exclude boundaries
    }
};

class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> result;
        SegmentTree segTree(nums);

        for (const auto& query : queries) {
            if (query[0] == 1) {
                int li = query[1], ri = query[2];
                result.push_back(segTree.query(li, ri));
            } else if (query[0] == 2) {
                int index = query[1], val = query[2];
                nums[index] = val;
                segTree.update(nums, index);
            }
        }

        return result;
    }
};
