#include<bits/stdc++.h>
#include<fstream>
using namespace std;
#define sz(a) (int)a.size()
#define ALL(v) v.begin(), v.end()
#define ALLR(v) v.rbegin(), v.rend()
#define ll long long
#define pb push_back
#define forr(i, a, b) for(int i = a; i < b; i++)
#define dorr(i, a, b) for(int i = a; i >= b; i--)
#define ld long double
#define vt vector
#include<fstream>
#define fi first
#define se second
#define pll pair<ll, ll>
#define pii pair<int, int>
#define mpp make_pair
const int mxn = 1e5 + 5;
class Solution {
public:
    int n;
    int bit[mxn + 1];
    void upd(int p, int v){
        p++;
        while(p <= n + 1){
            bit[p] += v; p += p & (-p);
        }
    }
    int get(int p){
        p++;
        int ans = 0;
        while(p){
            ans += bit[p]; p -= p & (-p);
        }
        return(ans);
    }
    bool check(vt<int>&nums, int id){
        if(id <= 0 || id >= sz(nums) - 1)return(0);
        return(nums[id] > nums[id - 1] && nums[id] > nums[id + 1]);
    }
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        n = sz(nums);
        for(int i = 1; i <= n + 1; i++)bit[i] = 0;
        for(int i = 1; i < sz(nums) - 1; i++){
            if(check(nums, i))upd(i, 1);
        }
        vt<int>ans;
        for(int i = 0; i < sz(queries); i++){
            int idq = queries[i][0];
            if(idq == 1){
                int l = queries[i][1], r = queries[i][2];
                if(l == r)ans.pb(0);
                else ans.pb(get(r - 1) - get(l));
            }else{
                int id = queries[i][1], v = queries[i][2];
                if(check(nums, id))upd(id, -1);
                if(check(nums, id - 1))upd(id - 1, -1);
                if(check(nums, id + 1))upd(id + 1, -1);
                nums[id] = v;
                if(check(nums, id))upd(id, 1);
                if(check(nums, id - 1))upd(id - 1, 1);
                if(check(nums, id + 1))upd(id + 1, 1);
            }
        }
        return(ans);
    }
};
