class SegmentTree:
    def __init__(self, data):
        self.n = len(data)
        self.tree = [0] * (2 * self.n)
        self.build(data)

    def build(self, data):
        for i in range(self.n):
            self.tree[self.n + i] = data[i]
        for i in range(self.n - 1, 0, -1):
            self.tree[i] = self.tree[2 * i] + self.tree[2 * i + 1]

    def update(self, index, value):
        pos = index + self.n
        self.tree[pos] = value
        while pos > 1:
            pos //= 2
            self.tree[pos] = self.tree[2 * pos] + self.tree[2 * pos + 1]

    def query(self, l, r):
        result = 0
        r+=1
        l += self.n
        r += self.n
        while l < r:
            if l % 2:
                result += self.tree[l]
                l += 1
            if r % 2:
                r -= 1
                result += self.tree[r]
            l //= 2
            r //= 2
        return result

class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        n = len(nums)
        prefix = [0]*n
        def check_peak(i):
            if i == 0 or i == n-1:
                return False
            return nums[i] > nums[i-1]  and nums[i] > nums[i+1]
        ans = []
        for i in range(1, n):
            if check_peak(i):
                prefix[i] += 1
        seg_tree = SegmentTree(prefix)

        for query in queries:
            if query[0] == 1:
                l, r = query[1], query[2]
                ans.append(seg_tree.query(l+1, r-1))
            else:
                index, val = query[1], query[2]
                nums[index] = val
                seg_tree.update(index, int(check_peak(index)))
                if index + 1 < n:
                    seg_tree.update(index+1, int(check_peak(index+1)))
                if index > 0:
                    seg_tree.update(index-1, int(check_peak(index-1)))
        return ans
