class Solution {
public:
    vector<int> countOfPeaks(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        vector<int> a(n+1);
        for(int i=1;i<n-1;i++){
            if(nums[i]>nums[i-1]&&nums[i]>nums[i+1])a[i+1]=1;
        }
        vector<int> tr(n+1);
        for(int i=1;i<=n;i++){
            tr[i]+=a[i];
            int nxt=i+(i&-i);
            if(nxt<=n)tr[nxt]+=tr[i];
        }
        vector<int> ans;
        auto update=[&](int l)->void{
            if(l<=0||l>=n-1)return;
            int cur=a[l+1];
            if(nums[l]>nums[l-1]&&nums[l]>nums[l+1])a[l+1]=1;
            else a[l+1]=0;
            int d=a[l+1]-cur;
            for(int i=l+1;i<=n;i+=i&-i)tr[i]+=d;
        };

        for(auto &q:queries){
            if(q[0]==1){
                int l=q[1],r=q[2];
                if(l==r){
                    ans.push_back(0);
                    continue;
                }

                int res=0;
                for(int i=r;i>0;i-=i&-i){
                    res+=tr[i];
                }
                for(int i=l+1;i>0;i-=i&-i){
                    res-=tr[i];
                }
                ans.push_back(res);
            }else{
                int l=q[1],val=q[2];
                nums[l]=val;
                update(l);
                update(l-1);
                update(l+1);
                // for(int i=1;i<=n;i++)cout<<tr[i]<<endl;
            }
        }
        return ans;
    }
};
