class SegmentTree:
    def __init__(self, nums):
        self.n = len(nums)
        self.tree = [0] * (4 * self.n)
        self.nums = nums
        self.build(0, 0, self.n - 1)

    def is_peak(self, i):
        if i <= 0 or i >= len(self.nums) - 1:
            return False
        return self.nums[i] > self.nums[i - 1] and self.nums[i] > self.nums[i + 1]

    def build(self, node, start, end):
        if start == end:
            self.tree[node] = 1 if self.is_peak(start) else 0
        else:
            mid = (start + end) // 2
            left_child = 2 * node + 1
            right_child = 2 * node + 2
            self.build(left_child, start, mid)
            self.build(right_child, mid + 1, end)
            self.tree[node] = self.tree[left_child] + self.tree[right_child]

    def query(self, node, start, end, L, R):
        if start > R or end < L:
            return 0
        if start >= L and end <= R:
            return self.tree[node]
        mid = (start + end) // 2
        left_child = 2 * node + 1
        right_child = 2 * node + 2
        left_peaks = self.query(left_child, start, mid, L, R)
        right_peaks = self.query(right_child, mid + 1, end, L, R)
        return left_peaks + right_peaks

    def update(self, node, start, end, idx):
        if start == end:
            self.tree[node] = 1 if self.is_peak(start) else 0
        else:
            mid = (start + end) // 2
            left_child = 2 * node + 1
            right_child = 2 * node + 2
            if start <= idx <= mid:
                self.update(left_child, start, mid, idx)
            else:
                self.update(right_child, mid + 1, end, idx)
            self.tree[node] = self.tree[left_child] + self.tree[right_child]

    def update_value(self, idx, value):
        self.nums[idx] = value
        for i in range(max(0, idx - 1), min(self.n, idx + 2)):
            self.update(0, 0, self.n - 1, i)

class Solution:
    def countOfPeaks(self, nums: List[int], queries: List[List[int]]) -> List[int]:
        seg_tree = SegmentTree(nums)
        results = []

        for query in queries:
            if query[0] == 1:
                li, ri = query[1], query[2]
                if ri - li < 2:
                    results.append(0)
                else:
                    results.append(seg_tree.query(0, 0, seg_tree.n - 1, li + 1, ri - 1))
            elif query[0] == 2:
                indexi, vali = query[1], query[2]
                seg_tree.update_value(indexi, vali)

        return results
