class Solution:
    def minimumArea(self, grid: List[List[int]], i1, i2, j1, j2) -> int:
        x0, x1, y0, y1 = inf, -1, inf, -1
        for i in range(i1, i2):
            for j in range(j1, j2):
                if grid[i][j]:
                    x0 = min(x0, i)
                    x1 = max(x1, i)
                    y0 = min(y0, j)
                    y1 = max(y1, j)
        return 0 if x1 < x0 or y1 < y0 else (x1 - x0 + 1) * (y1 - y0 + 1)

    def minimumSum(self, grid: List[List[int]]) -> int:
        ans = math.inf
        for _ in range(2):
            grid = list(zip(*grid))
            n, m = len(grid), len(grid[0])
            for i in range(1, n):
                for j in range(1, m):
                    r1 = self.minimumArea(grid, 0, i, 0, j)
                    r2 = self.minimumArea(grid, 0, i, j, m)
                    r3 = self.minimumArea(grid, i, n, 0, m)
                    if r1 and r2 and r3:
                        ans = min(ans, r1 + r2 + r3)
                    r1 = self.minimumArea(grid, 0, i, 0, m)
                    r2 = self.minimumArea(grid, i, n, 0, j)
                    r3 = self.minimumArea(grid, i, n, j, m)
                    if r1 and r2 and r3:
                        ans = min(ans, r1 + r2 + r3)
            for i1 in range(1, n):
                for i2 in range(i1 + 1, n):
                    r1 = self.minimumArea(grid, 0, i1, 0, m)
                    r2 = self.minimumArea(grid, i1, i2, 0, m)
                    r3 = self.minimumArea(grid, i2, n, 0, m)
                    if r1 and r2 and r3:
                        ans = min(ans, r1 + r2 + r3)
            for j1 in range(1, m):
                for j2 in range(j1 + 1, m):
                    r1 = self.minimumArea(grid, 0, n, 0, j1)
                    r2 = self.minimumArea(grid, 0, n, j1, j2)
                    r3 = self.minimumArea(grid, 0, n, j2, m)
                    if r1 and r2 and r3:
                        ans = min(ans, r1 + r2 + r3)
        return ans
