class Solution {
public:
    int n, m;
    int minimumSum(vector<vector<int>>& g) {
        n = g.size(), m = g[0].size();
        int res = 10000000;
        // 三横
        for (int ed1 = 0; ed1 < n; ed1 ++) {
            for (int ed2 = ed1 + 1; ed2 + 1 < n; ed2 ++) {
                int a = cal(g, 0, ed1, 0, m - 1), b = cal(g, ed1 + 1, ed2, 0, m - 1), c = cal(g, ed2 + 1, n - 1, 0, m - 1);
                if (a > 0 and b > 0 and c > 0) {
                    res = min(res, a + b + c);
                }
            }
        }
        // 三竖
        for (int ed1 = 0; ed1 < m; ed1 ++) {
            for (int ed2 = ed1 + 1; ed2 + 1 < m; ed2 ++) {
                int a = cal(g, 0, n - 1, 0, ed1), b = cal(g, 0, n - 1, ed1 + 1, ed2), c = cal(g, 0, n - 1, ed2 + 1, m - 1);
                if (a > 0 and b > 0 and c > 0) {
                    res = min(res, a + b + c);
                }
            }
        }

        // 上下1+2
        for (int i = 0; i + 1 < n; i ++) {
            int a = cal(g, 0, i, 0, m - 1);
            if (a <= 0) continue;
            for (int j = 0; j + 1 < m; j ++) {
                int b = cal(g, i + 1, n - 1, 0, j), c = cal(g, i + 1, n - 1, j + 1, m - 1);
                if (b > 0 and c > 0) {
                    res = min(res, a + b + c);
                }
            }
        }

        // 上下2+1
        for (int i = 1; i < n; i ++) {
            int a = cal(g, i, n - 1, 0, m - 1);
            if (a <= 0) continue;
            for (int j = 0; j + 1 < m; j ++) {
                int b = cal(g, 0, i - 1, 0, j), c = cal(g, 0, i - 1, j + 1, m - 1);
                if (b > 0 and c > 0) {
                    res = min(res, a + b + c);
                }
            }
        }

        // 左右1+2
        for (int i = 0; i + 1 < m; i ++) {
            int a = cal(g, 0, n - 1, 0, i);
            if (a <= 0) continue;
            for (int j = 0; j + 1 < n; j ++) {
                int b = cal(g, 0, j, i + 1, m - 1), c = cal(g, j + 1, n - 1, i + 1, m - 1);
                if (b > 0 and c > 0) {
                    res = min(res, a + b + c);
                }
            }
        }

        // 左右2+1
        for (int i = 1; i < m; i ++) {
            int a = cal(g, 0, n - 1, i, m - 1);
            if (a <= 0) continue;
            for (int j = 0; j + 1 < n; j ++) {
                int b = cal(g, 0, j, 0, i - 1), c = cal(g, j + 1, n - 1, 0, i - 1);
                if (b > 0 and c > 0) {
                    res = min(res, a + b + c);
                }
            }
        }
        return res;
    }
    int cal(vector<vector<int>>& grid, int r1, int r2, int c1, int c2) {
        int l = m, r = 0, up = n, down = 0;
        for (int i = r1; i <= r2; i ++) {
            for (int j = c1; j <= c2; j ++) {
                if (grid[i][j]) {
                    l = min(l, j);
                    r = max(r, j);
                    up = min(up, i);
                    down = max(down, i);
                }
            }
        }
        if (down < up or r < l) return 0;
        return (down - up + 1) * (r - l + 1);
    }
};
