int f(vector<vector<int>>& grid,int top,int bottom,int l,int r)
{
    int L=r,R=l-1,Top=bottom,Bottom=top-1;
    for(int i=top;i<bottom;i++)
    {
        for(int j=l;j<r;j++)
        {
            if(grid[i][j])
            {
                L=min(L,j);
                R=max(R,j);
                Top=min(Top,i);
                Bottom=max(Bottom,i);
            }
        }
    }
    if(L==r)
        return 1e8;
    return (Bottom-Top+1)*(R-L+1);
}
class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ans=1e8;
        for(int i=1;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                int sum=f(grid,0,i,0,m)+f(grid,i,j,0,m)+f(grid,j,n,0,m);
                ans=min(ans,sum);
            }
        }
        for(int i=1;i<m;i++)
        {
            for(int j=i+1;j<m;j++)
            {
                int sum=f(grid,0,n,0,i)+f(grid,0,n,i,j)+f(grid,0,n,j,m);
                ans=min(ans,sum);
            }
        }
        for(int i=1;i<n;i++)
        {
            for(int j=1;j<m;j++)
            {
                int sum=f(grid,0,i,0,j)+f(grid,0,i,j,m)+f(grid,i,n,0,m);
                ans=min(ans,sum);
                sum=f(grid,0,i,0,m)+f(grid,i,n,j,m)+f(grid,i,n,0,j);
                ans=min(ans,sum);
            }
        }
        for(int i=1;i<m;i++)
        {
            for(int j=1;j<n;j++)
            {
                int sum=f(grid,0,j,0,i)+f(grid,j,n,0,i)+f(grid,0,n,i,m);
                ans=min(ans,sum);
                sum=f(grid,0,n,0,i)+f(grid,0,j,i,m)+f(grid,j,n,i,m);
                //cout<<i<<" "<<j<<" "<<sum<<endl;
                ans=min(ans,sum);
            }
        }
        return ans;
    }
};
