class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        n,m = len(grid), len(grid[0])

        @lru_cache(None)
        def chk(x1,y1,x2,y2) -> int:
            xmin,xmax,ymin,ymax,f = n,0,m,0,0
            for i in range(x1,x2+1):
                for j in range(y1,y2+1):
                    if grid[i][j]:
                        f = 1
                        xmin = min(xmin, i)
                        xmax = max(xmax, i)
                        ymin = min(ymin, j)
                        ymax = max(ymax, j)
            if f:
                return (xmax - xmin + 1) * (ymax - ymin + 1)
            return 9999999999999


        @lru_cache(None)
        def dfs(t, x1,y1,x2,y2):
            if t == 0:
                return chk(x1,y1,x2,y2)
            if t == 1:
                ret = 999999999
                for i in range(x1+1,x2+1):
                    ret = min(ret, dfs(t-1,x1,y1,i-1,y2)+dfs(t-1,i,y1,x2,y2))
                for i in range(y1+1,y2+1):
                    ret = min(ret, dfs(t-1,x1,y1,x2,i-1)+dfs(t-1,x1,i,x2,y2))
                # print(t,x1,y1,x2,y2,ret)
                return ret
            else:
                ret = 999999999
                for i in range(x1+1,x2+1):
                    ret = min(ret, dfs(t-1,x1,y1,i-1,y2)+dfs(t-2,i,y1,x2,y2), dfs(t-2,x1,y1,i-1,y2)+dfs(t-1,i,y1,x2,y2))
                for i in range(y1+1,y2+1):
                    ret = min(ret, dfs(t-1,x1,y1,x2,i-1)+dfs(t-2,x1,i,x2,y2), dfs(t-2,x1,y1,x2,i-1)+dfs(t-1,x1,i,x2,y2))
                # print(t,x1,y1,x2,y2,ret)
                return ret

        return dfs(2,0,0,n-1,m-1)
