fn min_area(grid: &Vec<Vec<i32>>, i1: usize, j1: usize, i2: usize, j2: usize) -> i32 {
    let mut min_i = 10000;
    let mut min_j = 10000;
    let mut max_i = 0;
    let mut max_j = 0;
    for i in i1..i2 {
        for j in j1..j2 {
            if grid[i][j] == 1 {
                min_i = min_i.min(i);
                min_j = min_j.min(j);
                max_i = max_i.max(i);
                max_j = max_j.max(j);
            }
        }
    }
    if min_i > max_i || min_j > max_j {
        return 0;
    }
    ((max_i - min_i + 1) * (max_j - min_j + 1)) as i32
}

impl Solution {
    pub fn minimum_sum(grid: Vec<Vec<i32>>) -> i32 {
        // 4 cases: --, -|, |-, ||

        let m = grid.len();
        let n = grid[0].len();
        let mut result = 1000000;

        // --
        for i1 in 1..m {
            for i2 in (i1 + 1)..m {
                let a = min_area(&grid, 0, 0, i1, n);
                let b = min_area(&grid, i1, 0, i2, n);
                let c = min_area(&grid, i2, 0, m, n);
                if a > 0 && b > 0 && c > 0 {
                    result = result.min(a + b + c);
                }
            }
        }

        // ||
        for j1 in 1..n {
            for j2 in (j1 + 1)..n {
                let a = min_area(&grid, 0, 0, m, j1);
                let b = min_area(&grid, 0, j1, m, j2);
                let c = min_area(&grid, 0, j2, m, n);
                if a > 0 && b > 0 && c > 0 {
                    result = result.min(a + b + c);
                }
            }
        }

        for i in 1..m {
            for j in 1..n {
                {
                    // -|
                    let a = min_area(&grid, i, 0, m, n);
                    let b = min_area(&grid, 0, 0, i, j);
                    let c = min_area(&grid, 0, j, i, n);
                    if a > 0 && b > 0 && c > 0 {
                        result = result.min(a + b + c);
                    }
                }
                {
                    // -|
                    let a = min_area(&grid, 0, 0, i, n);
                    let b = min_area(&grid, i, 0, m, j);
                    let c = min_area(&grid, i, j, m, n);
                    if a > 0 && b > 0 && c > 0 {
                        result = result.min(a + b + c);
                    }
                }

                {
                    // |-
                    let a = min_area(&grid, 0, j, m, n);
                    let b = min_area(&grid, 0, 0, i, j);
                    let c = min_area(&grid, i, 0, m, j);
                    if a > 0 && b > 0 && c > 0 {
                        result = result.min(a + b + c);
                    }
                }
                {
                    // |-
                    let a = min_area(&grid, 0, 0, m, j);
                    let b = min_area(&grid, 0, j, i, n);
                    let c = min_area(&grid, i, j, m, n);
                    if a > 0 && b > 0 && c > 0 {
                        result = result.min(a + b + c);
                    }
                }
            }
        }

        result
    }
}
