class Solution {
public:
    int area(int x1, int y1, int x2, int y2, vector<vector<int>> &a){
        int n = a.size(), m = a[0].size();
        int x_min = n, x_max = 0, y_min = m, y_max = 0;
        for(int i = x1; i <= x2; i++){
            for(int j = y1; j <= y2; j++){
                if(a[i][j] == 1){
                    x_min = min(x_min, i);
                    x_max = max(x_max, i);
                    y_min = min(y_min, j);
                    y_max = max(y_max, j);
                }
            }
        }

        if(x_min <= x_max && y_min <= y_max){
            return max(1, (x_max-x_min+1)*(y_max-y_min+1));
        }else{
            return 1;
        }

    }
    int f(int x, int y, vector<vector<int>> &a, int n, int m){
        int a1 = area(0, 0, x, y, a);
        int a2 = area(0, y+1, x, m-1, a);
        int a3 = area(x+1, 0, n-1, y, a);
        int a4 = area(x+1, y+1, n-1, m-1, a);

        int b1 = area(0, 0, x, m-1, a);
        int b2 = area(x+1, 0, n-1, m-1, a);
        int b3 = area(0, 0, n-1, y, a);
        int b4 = area(0, y+1, n-1, m-1, a);

        int ans = INT_MAX;

        ans = min(ans, a1+a3+b4);
        ans = min(ans, a2+a4+b3);
        ans = min(ans, a1+a2+b2);
        ans = min(ans, a3+a4+b1);

        return ans;
    }
    int minimumSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = INT_MAX;
        for(int x = 0; x + 1 < n; x++){
            for(int y = 0; y + 1 < m; y++){
                ans = min(ans, f(x, y, a, n, m));
            }
        }
        for(int r1 = 0; r1 + 2 < n; r1++){
            for(int r2 = r1+1; r2 + 1 < n; r2++){
                ans = min(ans, area(0, 0, r1, m-1, a) + area(r1+1, 0, r2, m-1, a) + area(r2+1, 0, n-1, m-1, a));
            }
        }
        for(int c1 = 0; c1 + 2 < m; c1++){
            for(int c2 = c1+1; c2 + 1 < m; c2++){
                ans = min(ans, area(0, 0, n-1, c1, a) + area(0, c1+1, n-1, c2, a) + area(0, c2+1, n-1, m-1, a));
            }
        }
        return ans;
    }
};
