class Solution {
public:
    int minrect(vector<vector<int>>& g, int rl, int rr, int rt, int rd){
        int l=1e9, r=-1, t=1e9, d=-1;
        for(int i=rt; i<rd; i++){
            for(int j=rl; j<rr; j++){
                if(g[i][j] == 1){
                    l = min(l, j);
                    r = max(r, j);
                    t = min(t, i);
                    d = max(d, i);
                }
            }
        }
        if(r<l || d<t) return 0;
        return (r-l+1)*(d-t+1);
    }

    int split(vector<vector<int>>& g, int l, int r, int t, int d){
        int m = 1e9;
        for(int i=t+1; i<d; i++){
            m = min(m, minrect(g, l, r, t, i) + minrect(g, l, r, i, d));
        }
        for(int j=l+1; j<r; j++){
            m = min(m, minrect(g, l, j, t, d) + minrect(g, j, r, t, d));
        }
        return m;
    }

    int minimumSum(vector<vector<int>>& grid) {
        int m=grid.size(), n=grid[0].size();
        int ans = 1e9;
        for(int i=1; i<m; i++){
            ans = min(ans, minrect(grid, 0, n, 0, i) + split(grid, 0, n, i, m));
            ans = min(ans, minrect(grid, 0, n, i, m) + split(grid, 0, n, 0, i));
        }
        for(int j=1; j<n; j++){
            ans = min(ans, minrect(grid, 0, j, 0, m) + split(grid, j, n, 0, m));
            ans = min(ans, minrect(grid, j, n, 0, m) + split(grid, 0, j, 0, m));
        }
        return ans;
    }
};
