public class Solution {
    public int MinimumSum(int[][] grid) {
        int n = grid.Length, m = grid[0].Length;
        int ans = 1000000;
        //左中右
        for(int i = 0; i < m; i++)
        {
            for(int j = i + 1; j < m; j++)
            {
                int s1 = GetMin(grid, 0, i, 0, n - 1);
                int s2 = GetMin(grid, i + 1, j, 0, n - 1);
                int s3 = GetMin(grid, j + 1, m - 1, 0, n - 1);
                if(s1 > 0 && s2 > 0 && s3 > 0)
                {
                    ans = Math.Min(ans, s1 + s2 + s3);
                }
            }
        }
        //上中下
        for(int i = 0; i < n; i++)
        {
            for(int j = i + 1; j < n; j++)
            {
                int s1 = GetMin(grid, 0, m - 1, 0, i);
                int s2 = GetMin(grid, 0, m - 1, i + 1, j);
                int s3 = GetMin(grid, 0, m - 1, j + 1, n - 1);
                if(s1 > 0 && s2 > 0 && s3 > 0)
                {
                    ans = Math.Min(ans, s1 + s2 + s3);
                }
            }
        }
        //上下|右
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                int s1 = GetMin(grid, 0, i, 0, j);
                int s2 = GetMin(grid, 0, i, j + 1, n - 1);
                int s3 = GetMin(grid, i + 1, m - 1, 0, n - 1);
                if(s1 > 0 && s2 > 0 && s3 > 0)
                {
                    ans = Math.Min(ans, s1 + s2 + s3);
                }
            }
        }
        //左|上下
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                int s1 = GetMin(grid, 0, i, 0, n - 1);
                int s2 = GetMin(grid, i + 1, m - 1, 0, j);
                int s3 = GetMin(grid, i + 1, m - 1, j + 1, n - 1);
                if(s1 > 0 && s2 > 0 && s3 > 0)
                {
                    ans = Math.Min(ans, s1 + s2 + s3);
                }
            }
        }
        //左右|下
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                int s1 = GetMin(grid, 0, i, 0, j);
                int s2 = GetMin(grid, i + 1, m - 1, 0, j);
                int s3 = GetMin(grid, 0, m - 1, j + 1, n - 1);
                if(s1 > 0 && s2 > 0 && s3 > 0)
                {
                    ans = Math.Min(ans, s1 + s2 + s3);
                }
            }
        }
        //上|左右
        for(int i = 0; i < m; i++)
        {
            for(int j = 0; j < n; j++)
            {
                int s1 = GetMin(grid, 0, m - 1, 0, j);
                int s2 = GetMin(grid, 0, i, j + 1, n - 1);
                int s3 = GetMin(grid, i + 1, m - 1, j + 1, n - 1);
                if(s1 > 0 && s2 > 0 && s3 > 0)
                {
                    ans = Math.Min(ans, s1 + s2 + s3);
                }
            }
        }
        return ans;
    }

    private int GetMin(int[][] grid, int L, int R, int T, int B)
    {
        int n = grid.Length, m = grid[0].Length;
        int l, r, t, b; l = m; t = n; r = b = 0;
        bool valid = false;
        for(int i = T; i <= B; i++) 
        {
            for(int j = L; j <= R; j++)
            {
                if(grid[i][j] == 1)
                {
                    valid = true;
                    l = Math.Min(l, j);
                    r = Math.Max(r, j);
                    t = Math.Min(t, i);
                    b = Math.Max(b, i);
                }
            }
        }
        if(!valid) return 0;
        return (r - l + 1) * (b - t + 1);
    }
}
