class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def do(x,y,p,q):
            sm, sn = p - x + 1, q - y + 1
            a,b,c,d  =  m, n, 0, 0
            for i in range(x, p+1):
                for j in range(y, q+1):
                    if grid[i][j]:
                        a = min(a, i)
                        b = min(b, j)
                        c = max(c, i)
                        d = max(d, j)
            if (a,b,c,d)  ==  (m, n, 0, 0): return 1
            return (c-a+1) * (d-b+1)

        m, n = len(grid), len(grid[0])
        ans = float("inf")
        for i in range(m-1):
            for j in range(i):
                ans = min(ans, do(0,0,j,n-1)+do(j+1,0,i,n-1)+do(i+1,0,m-1,n-1))             
            for j in range(n-1):
                ans = min(ans, do(0,0,i,n-1)+do(i+1,0,m-1,j)+do(i+1,j+1,m-1,n-1), do(0,0,i,j)+do(0,j+1,i,n-1)+do(i+1,0,m-1,n-1))
        for j in range(n-1):
            for i in range(j):
                ans = min(ans, do(0,0,m-1,i)+do(0,i+1,m-1,j)+do(0,j+1,m-1,n-1))             
            for i in range(m-1):
                ans = min(ans, do(0,0,i,j)+do(i+1,0,m-1,j)+do(0,j+1,m-1,n-1), do(0,0,m-1,j)+do(0,j+1,i,n-1)+do(i+1,j+1,m-1,n-1))

        return ans
