typedef uint64_t u64;

class Solution {

  int ma(u64 x[], int n) {
    u64 z = 0;
    int a = -1, b = 0;
    for (int i = 0; i < n; ++i) {
        z |= x[i];
        if (a==-1 && z) a = i;
        if (x[i]) b = i;
    }
    return z ? (b - a + 1) * (64 - __builtin_clzll(z) - __builtin_ctzll(z)) : 0;
  }

  int ma2(u64 x[], int n, int m) {
    if (n == 0 || m == 0) return 0;
    int res = 1000000;
    for (int i = 0; i < n; ++i)
        res = min(res, ma(x, i) + ma(x + i, n - i));
    u64 y[64];
    fill_n(y, m, 0);
    for (int i = 0; i < n; ++i) for (int j = 0; j < m; ++j)
        if (x[i] >> j & 1) y[j] |= 1ULL << i;
    for (int i = 0; i < m; ++i)
        res = min(res, ma(y, i) + ma(y + i, m - i));
    return res;
  }

  int sol(u64 x[], int n, int m) {
    int res = 10000000;
    for (int i = 0; i < n; ++i) {
        res = min(res, ma(x, i) + ma2(x + i, n - i, m));
        res = min(res, ma2(x, i, m) + ma(x + i, n - i));
    }
    return res;
  }

public:
  int minimumSum(vector<vector<int>>& g) {
    const int n = g.size(), m = g[0].size();
    vector<u64> h(n, 0), v(m, 0);
    for (int i = 0; i < n; ++i) for (int j = 0; j < m; ++j) if(g[i][j]) {
        h[i] |= 1ULL << j;
        v[j] |= 1ULL << i;
    }
    return min(sol(&h[0], n, m), sol(&v[0], m, n));
  }
};
