class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int m = grid.size(), n = grid[0].size(), ans = 2e9;
        int mnx = 1e9, mny = 1e9, mxx = 0, mxy = 0;
        for (int i = 0; i < m; i ++ ) {
            for (int j = 0; j < n; j ++ ) {
                if (grid[i][j]) {
                    mnx = min(mnx, i + 1);
                    mxx = max(mxx, i + 1);
                    mny = min(mny, j + 1);
                    mxy = max(mxy, j + 1);
                }
            }
        }
        auto cal = [&](int lx,int rx,int ly,int ry) -> int {
            --lx,--ly,--rx,--ry;
            int mnx = 100, mny = 100, mxx = 0, mxy = 0;
            for (int i = lx; i <= rx; i ++ ) {
                for (int j = ly; j <= ry; j ++ ) {
                    if (grid[i][j]) {
                        mnx = min(mnx, i + 1);
                        mxx = max(mxx, i + 1);
                        mny = min(mny, j + 1);
                        mxy = max(mxy, j + 1);
                    }
                }
            }
            return (mxx - mnx + 1) * (mxy - mny + 1);
        };
        for (int j = mny; j <= mxy - 2; j ++ ) {
            for (int k = j + 1; k <= mxy - 1; k ++ ) {
                ans = min(ans, cal(mnx, mxx, mny, j) + cal(mnx, mxx, j + 1, k) + cal(mnx, mxx, k + 1, mxy));
            }
        }
        for (int j = mnx; j <= mxx - 2; j ++ ) {
            for (int k = j + 1; k <= mxx - 1; k ++ ) {
                ans = min(ans, cal(mnx, j, mny, mxy) + cal(j + 1, k, mny, mxy) + cal(k + 1, mxx, mny, mxy));
            }
        }
        for (int j = mny; j <= mxy - 1; j ++ ) {
            for (int k = mnx; k <= mxx - 1; k ++ ) {
                ans = min(ans, cal(mnx, mxx, mny, j) + cal(mnx, k, j + 1, mxy) + cal(k + 1, mxx, j + 1, mxy));
            }
        }
        for (int j = mxy; j > mny; j -- ) {
            for (int k = mnx; k <= mxx - 1; k ++ ) {
                ans = min(ans, cal(mnx, mxx, j, mxy) + cal(mnx, k, mny, j - 1) + cal(k + 1, mxx, mny, j - 1));
            }
        }
        for (int j = mnx; j <= mxx - 1; j ++ ) {
            for (int k = mny; k <= mxy - 1; k ++ ) {
                ans = min(ans, cal(mnx, j, mny, mxy) + cal(j + 1, mxx, mny, k) + cal(j + 1, mxx, k + 1, mxy));
            }
        }
        for (int j = mxx; j > mnx; j -- ) {
            for (int k = mny; k <= mxy - 1; k ++ ) {
                ans = min(ans, cal(j, mxx, mny, mxy) + cal(mnx, j - 1, mny, k) + cal(mnx, j - 1, k + 1, mxy));
            }
        }
        return ans;
    }
};
