class Solution {
    public int CalculateRectangleArea(int t, int b, int l, int r, int[][] grid){
        int ti = 1000, bi = -1, li = 1000, ri = -1;
        boolean flag = false;
        for(int i = t; i <= b; i++){
            for(int j = l; j <= r; j++){
                if(grid[i][j] == 1){
                    flag = true;
                    ti = Math.min(ti, i);
                    bi = Math.max(bi, i);
                    li = Math.min(li, j);
                    ri = Math.max(ri, j);
                }
            }
        }
        if(flag){
            return (bi-ti+1) * (ri-li+1);
        }
        return 1;
    }


    public int splitTwoWayrow(int t, int b, int l, int r, int[][] grid){
        int ans = 100000;
        for(int i = t; i < b; i++){
            int t1 = t, b1 = i, l1 = l, r1 = r;
            int t2 = i+1, b2 = b, l2 = l, r2 = r;
            ans = Math.min(ans, CalculateRectangleArea(t1, b1, l1, r1, grid)+CalculateRectangleArea(t2, b2, l2, r2, grid));
        }
        return ans;
    }

    public int splitTwoWaycol(int t, int b, int l, int r, int[][] grid){
        int ans = 100000;
        for(int i = l; i < r; i++){
            int t1 = t, b1 = b, l1 = l, r1 = i;
            int t2 = t, b2 = b,  l2 = i+1,  r2 = r;
            ans = Math.min(ans, CalculateRectangleArea(t1, b1, l1, r1, grid)+CalculateRectangleArea(t2, b2, l2, r2, grid));
        }
        return ans;

    }
    public int minimumSum(int[][] grid) {
        int ans = 100000;
        int n = grid.length, m = grid[0].length;
        for(int i = 0; i < n-1; i++){
            int t1 = 0, b1 = i, l1 = 0, r1 = m-1;
            int t2 = i+1, b2 = n-1, l2 = 0, r2 = m-1;
            ans = Math.min(ans, Math.min(splitTwoWayrow(t1,b1,l1,r1,grid), splitTwoWaycol(t1,b1,l1,r1,grid)) + CalculateRectangleArea(t2,b2,l2,r2,grid));
            ans = Math.min(ans, Math.min(splitTwoWayrow(t2,b2,l2,r2,grid), splitTwoWaycol(t2,b2,l2,r2,grid)) + CalculateRectangleArea(t1,b1,l1,r1,grid));
        }

        for(int i = 0; i < m-1; i++){
            int t1 = 0, b1 = n-1, l1 = 0, r1 = i;
            int t2 = 0, b2 = n-1, l2 = i+1, r2 = m-1;
            ans = Math.min(ans, Math.min(splitTwoWayrow(t1,b1,l1,r1,grid), splitTwoWaycol(t1,b1,l1,r1,grid)) + CalculateRectangleArea(t2,b2,l2,r2,grid));
            ans = Math.min(ans, Math.min(splitTwoWayrow(t2,b2,l2,r2,grid), splitTwoWaycol(t2,b2,l2,r2,grid)) + CalculateRectangleArea(t1,b1,l1,r1,grid));
        }

        return ans;

    }
}
