class Solution {
public:
    bool a[30][30];
    int calc(int nl,int nr,int ml,int mr)
    {
        int xl=nr;    int yl=mr;
        int xr=nl;    int yr=ml;
        for(int i=nl;i<=nr;++i)
            for(int j=ml;j<=mr;++j)
            {
                if(a[i][j]==0)  continue;
                xr=max(xr,i+1);
                yr=max(yr,j+1);
                xl=min(xl,i);
                yl=min(yl,j);
            }
        return max((xr-xl)*(yr-yl),1);
    }
    int solve(int nl,int nr,int ml,int mr,int s)
    {
        if(s==1)    return calc(nl,nr,ml,mr);
        int ans=100000;
        for(int i=nl;i<nr;++i)
        {
            ans=min(ans,solve(nl,i,ml,mr,1)+solve(i+1,nr,ml,mr,s-1));
            if(s==3)
                ans=min(ans,solve(nl,i,ml,mr,s-1)+solve(i+1,nr,ml,mr,1));
        }
        for(int i=ml;i<mr;++i)
        {
            ans=min(ans,solve(nl,nr,ml,i,1)+solve(nl,nr,i+1,mr,s-1));
            if(s==3)
            ans=min(ans,solve(nl,nr,ml,i,s-1)+solve(nl,nr,i+1,mr,1));
        }
        return ans;
    }
    int minimumSum(vector<vector<int>>& g) {
        int n=g.size();
        int m=g[0].size();
        for(int i=0;i<n;++i)
            for(int j=0;j<m;++j)
                a[i][j]=g[i][j];
        return solve(0,n-1,0,m-1,3);
    }
};
