class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:

        @cache
        def shrinkrect(rlow, rhigh, clow, chigh):
            rmin = float('inf')
            rmax = float('-inf')
            cmin = float('inf')
            cmax = float('-inf')
            for r in range(rlow, rhigh+1):
                for c in range(clow, chigh+1):
                    if grid[r][c] == 1:
                        rmin = min(rmin, r)
                        rmax = max(rmax, r)
                        cmin = min(cmin, c)
                        cmax = max(cmax, c)
            return (rmax-rmin+1)*(cmax-cmin+1)


        rows = len(grid)
        cols = len(grid[0])

        out = float('inf')
        for r1 in range(rows-1):
            for r2 in range(r1+1, rows-1):
                a = shrinkrect(0, r1, 0, cols-1)
                b = shrinkrect(r1+1, r2, 0, cols-1)
                c = shrinkrect(r2+1, rows-1, 0, cols-1)
                out =  min(out, a+b+c)
        for c1 in range(cols-1):
            for c2 in range(c1+1, cols-1):
                a = shrinkrect(0, rows-1, 0, c1)
                b = shrinkrect(0, rows-1, c1+1, c2)
                c = shrinkrect(0, rows-1, c2+1, cols-1)
                out = min(out, a+b+c)
        for r1 in range(rows-1):
            for c1 in range(cols-1):
                a = shrinkrect(0, r1, 0, cols-1)
                b = shrinkrect(r1+1, rows-1, 0, c1)
                c = shrinkrect(r1+1, rows-1, c1+1, cols-1)
                out = min(out, a+b+c)

                a = shrinkrect (r1+1, rows-1, 0, cols-1)
                b = shrinkrect(0, r1, 0, c1)
                c = shrinkrect(0, r1, c1+1, cols-1)
                out = min(out, a+b+c)

        for c1 in range(cols-1):
            for r1 in range(rows-1):
                a = shrinkrect(0, rows-1, 0, c1)
                b = shrinkrect(0, r1, c1+1, cols-1)
                c = shrinkrect(r1+1, rows-1, c1+1, cols-1)
                out = min(out, a+b+c)

                a = shrinkrect(0, rows-1, c1+1, cols-1)
                b = shrinkrect(0, r1, 0, c1)
                c = shrinkrect(r1+1, rows-1, 0, c1)
                out = min(out, a+b+c)
        return out
