class Solution {
public:
    int m,n;
    int getAns(int sx,int sy,int tx,int ty,vector<vector<int>>& grid) {
        int top = 1e9,bottom = 0,left = 1e9,right = 0;
        for(int i=sx;i<=tx;i++){
            for(int j=sy;j<=ty;j++){
                if(grid[i][j]==1){
                    top = min(top,i);
                    bottom = max(bottom,i);
                    left = min(left,j);
                    right = max(right,j);
                }
            }
        }
        if(top==1e9) return 1;
        int length = bottom - top + 1;
        int width  = right-left+1;
        return length*width;   
    }
    int minimumSum(vector<vector<int>>& grid) {
        m = grid.size();
        n = grid[0].size();
        int ans = 1e9;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                //Divide here;
                if(i==m-1 && j==n-1) continue;
                int curAns = getAns(0,0,i,j,grid);
                if(j<n-1 && i<m-1){
                    int op1 = getAns(0,j+1,i,n-1,grid) + getAns(i+1,0,m-1,n-1,grid);
                    int op2 = getAns(0,j+1,m-1,n-1,grid) + getAns(i+1,0,m-1,j,grid);
                    ans = min(ans,curAns+op1);
                    ans = min(ans,curAns+op2);
                }
                else if(j==n-1){
                    for(int k=0;k<n-1;k++){
                        int op = getAns(i+1,0,m-1,k,grid) + getAns(i+1,k+1,m-1,n-1,grid);
                            ans = min(ans,curAns+op);
                    }
                    if(i<m-1){
                        for(int k=i+1;k<m-1;k++){
                            int op = getAns(i+1,0,k,n-1,grid) + getAns(k+1,0,m-1,n-1,grid);
                            ans = min(ans,curAns+op);
                        }
                    }
                }
                else if(i==m-1){
                    for(int k=0;k<m-1;k++){
                        int op = getAns(0,j+1,k,n-1,grid) + getAns(k+1,j+1,m-1,n-1,grid);
                        ans = min(ans,curAns+op);
                    }
                    if(j<n-1){
                        for(int k=j+1;k<n-1;k++){
                            int op = getAns(0,j+1,m-1,k,grid) + getAns(0,k+1,m-1,n-1,grid);
                            ans = min(ans,curAns+op);
                        }
                    }
                }

            }
        }
        return ans;

    }
};
