class Solution {
public:

    int dp[30][30][30][30][4];

    int solve(int rx, int ry, int cx, int cy, int t, vector<vector<int>>& a){
        if(rx > ry || cx > cy) return 0;
        if(dp[rx][ry][cx][cy][t] != -1) return dp[rx][ry][cx][cy][t];

        if(t == 1){
            int mnr = ry, mxr = -1, mnc = cy, mxc = -1;
            for(int i = rx; i <= ry; i++){
                for(int j = cx; j <= cy; j++){
                    if(a[i][j]){
                        mnr = min(mnr, i);
                        mxr = max(mxr, i);
                        mnc = min(mnc, j);
                        mxc = max(mxc, j);
                    }
                }
            }
            if(mxr == -1) return dp[rx][ry][cx][cy][t] = 0;
            return dp[rx][ry][cx][cy][t] = (mxr - mnr + 1) * (mxc - mnc + 1);
        }
        else if(t == 2){
            int mn = 1e9;
            for(int i = rx; i < ry; i++){
                mn = min(solve(rx, i, cx, cy, 1, a) + solve(i+1, ry, cx, cy, 1, a), mn);
            }
            for(int j = cx; j < cy; j++){
                mn = min(solve(rx, ry, cx, j, 1, a) + solve(rx, ry, j+1, cy, 1, a), mn);
            }
            return dp[rx][ry][cx][cy][t] = mn;
        }
        else{
            int mn = 1e9;
            for(int i = rx; i < ry; i++){
                mn = min(solve(rx, i, cx, cy, 2, a) + solve(i+1, ry, cx, cy, 1, a), mn);
                mn = min(solve(rx, i, cx, cy, 1, a) + solve(i+1, ry, cx, cy, 2, a), mn);
            }
            for(int j = cx; j < cy; j++){
                mn = min(solve(rx, ry, cx, j, 2, a) + solve(rx, ry, j+1, cy, 1, a), mn);
                mn = min(solve(rx, ry, cx, j, 1, a) + solve(rx, ry, j+1, cy, 2, a), mn);
            }
            return dp[rx][ry][cx][cy][t] = mn;
        }
    }

    int minimumSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        memset(dp, -1, sizeof(dp));

        return solve(0, n-1, 0, m-1, 3, a);
    }
};
