class Solution {

    public int func(int[][] grid, int rs, int re, int cs, int ce) {
        int minir = 31 ,maxir = -1, minic = 31, maxic=-1;
        for(int i=rs;i<=re;i++) {
            for(int j=cs;j<=ce;j++) {
                if(grid[i][j]==1) {
                    minir = Math.min(minir, i);
                    maxir = Math.max(maxir, i);
                    minic = Math.min(minic, j);
                    maxic = Math.max(maxic, j);

                }
            }
        }
        if(maxic ==-1) return 0;
        return (maxir-minir+1)*(maxic - minic+1);
    }

    public int pat1(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int ans = 10000000;
        for(int i=0;i<n-1;i++) {
            for(int j=i+1; j<n-1;j++) {
                int a1 = func(grid, 0, i, 0, m-1);
                int a2 = func(grid, i+1, j, 0,m-1);
                int a3 = func(grid, j+1, n-1, 0, m-1);
                ans = Math.min(ans, a1+a2+a3);
            }
        }
        return ans;
    }

