                int a1 = func(grid, 0, n-1, 0, i);
                int a2 = func(grid, 0, n-1, i+1,j);
                int a3 = func(grid, 0, n-1, j+1,m-1);
                ans = Math.min(ans, a1+a2+a3);
            }
        }
        return ans;
    }

