    public int minimumSum(int[][] grid) {
        int a1 = pat1(grid);
        int a2 = pat2(grid);
        int a3 = pat3(grid);
        int a4 = pat4(grid);
        int a5 = pat5(grid);
        int a6 = pat6(grid);
        int[] arr = new int[]{a1,a2,a3,a4,a5,a6};
        Arrays.sort(arr);
        return arr[0];
    }
