class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        auto minsquare=[&](int i1,int i2,int j1,int j2){            
            vector<int> col(m),row(n);
            for(int i=i1;i<=i2;++i){
                for(int j=j1;j<=j2;++j){
                    if(grid[i][j]){
                        col[j]=1;
                        row[i]=1;
                    }
                }
            }
            int mir=INT_MAX,mar=INT_MIN;
            int mic=INT_MAX,mac=INT_MIN;
            for(int i=i1;i<=i2;++i){
                if(row[i]){
                    mir=min(mir,i);
                    mar=max(mar,i);
                }
            }
            for(int j=j1;j<=j2;++j){
                if(col[j]){
                    mic=min(mic,j);
                    mac=max(mac,j);
                }
            }
            if(mir==INT_MAX) return 0;
            return (mar-mir+1)*(mac-mic+1);
        };
        int ans=INT_MAX;
        for(int g1=0;g1<n;++g1){
            for(int g2=g1+1;g2+1<n;++g2){
                int s1=minsquare(0,g1,0,m-1),s2=minsquare(g1+1,g2,0,m-1),s3=minsquare(g2+1,n-1,0,m-1);
                if(s1==0||s2==0||s3==0){
                    continue;
                }
                ans=min(ans,s1+s2+s3);
            }
        }
        for(int g1=0;g1<m;++g1){
            for(int g2=g1+1;g2+1<m;++g2){
                int s1=minsquare(0,n-1,0,g1),s2=minsquare(0,n-1,g1+1,g2),s3=minsquare(0,n-1,g2+1,m-1);
                if(s1==0||s2==0||s3==0){
                    continue;
                }
                ans=min(ans,s1+s2+s3);
            }
        }
        for(int g1=0;g1+1<n;++g1){
            for(int g2=0;g2+1<m;++g2){
                int s1=minsquare(0,n-1,0,g2),s2=minsquare(0,g1,g2+1,m-1),s3=minsquare(g1+1,n-1,g2+1,m-1);
                if(!(s1==0||s2==0||s3==0)){
                    ans=min(ans,s1+s2+s3);
                }
                s1=minsquare(0,g1,0,g2),s2=minsquare(g1,n-1,0,g2),s3=minsquare(0,n-1,g2+1,m-1);
                if(!(s1==0||s2==0||s3==0)){
                    ans=min(ans,s1+s2+s3);
                }
                s1=minsquare(0,g1,0,g2),s2=minsquare(0,g1,g2+1,m-1),s3=minsquare(g1+1,n-1,0,m-1);
                if(!(s1==0||s2==0||s3==0)){
                    ans=min(ans,s1+s2+s3);
                }
                s1=minsquare(0,g1,0,m-1),s2=minsquare(g1+1,n-1,0,g2),s3=minsquare(g1+1,n-1,g2+1,m-1);
                if(!(s1==0||s2==0||s3==0)){
                    ans=min(ans,s1+s2+s3);
                }
            }
        }
        // for(int g1=0;g1<m;++g1){
        //     for(int g2=g1+1;g2+1<m;++g2){
        //         int s1=minsquare(0,n-1,0,g1),s2=minsquare(0,n-1,g1+1,g2),s3=minsquare(0,n-1,g2+1,m-1);
        //         if(s1==0||s2==0||s3==0){
        //             continue;
        //         }
        //         ans=min(ans,s1+s2+s3);
        //     }
        // }
        return ans;
    }   
};
