class Solution {
public:
    #define ll long long int
    long long getans(int a,int b,int c,int d,vector<vector<int>> &grid)
    {
        long long l1=5000,l2=0,r1=5000,r2=0;
        for(long long i=a;i<=b;i++)
        {
            for(long long j=c;j<=d;j++)
            {
                if(grid[i-1][j-1]==1)
                {
                    l1=min(l1,j+1);
                    l2=max(l2,j+1);
                    r1=min(r1,i+1);
                    r2=i+1;
                }
            }
        }
        if(l1==5000) return 1e9;
        return (l2-l1+1)*(r2-r1+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        long long int ans=1e9;
        for(int i=1;i<n-1;i++)
   {        
           for(int k=i+1;k<n;k++)
           {
              long long int x1=getans(1,i,1,m,grid);
              long long int x2=getans(i+1,k,1,m,grid);
              long long int x3=getans(k+1,n,1,m,grid);
              ans=min(ans,x1+x2+x3);
           }
       }
   for(int i=1;i<m-1;i++)
   {
       for(int k=i+1;k<m;k++)
       {
            long long int x1=getans(1,n,1,i,grid);
              long long int x2=getans(1,n,i+1,k,grid);
              long long int x3=getans(1,n,k+1,m,grid);
              ans=min(ans,x1+x2+x3);
       }
   }
   for(int i=1;i<m;i++)
   {
       for(int j=1;j<n;j++)
       {
           long long int x1=getans(1,n,1,i,grid);
           long long int x2=getans(1,j,i+1,m,grid);
           long long int x3=getans(j+1,n,i+1,m,grid);
           ans=min(ans,x1+x2+x3);
       }
   }
   for(int i=1;i<m;i++)
   {
       for(int j=1;j<n;j++)
       {
           long long int x1=getans(1,j,1,i,grid);
           long long int x2=getans(j+1,n,1,i,grid);
           long long int x3=getans(1,n,i+1,m,grid);
           ans=min(ans,x1+x2+x3);
       }
   }
   for(int i=1;i<n;i++)
   {
       for(int j=1;j<m;j++)
       {
           long long int x1=getans(1,i,1,m,grid);
           long long int x2=getans(i+1,n,1,j,grid);
           long long int x3=getans(i+1,n,j+1,m,grid);
           ans=min(ans,x1+x2+x3);
       }
   }
   for(int i=1;i<n;i++)
   {
       for(int j=1;j<m;j++)
       {
           ll x1=getans(1,i,1,j,grid);
           ll x2=getans(1,i,j+1,m,grid);
           ll x3=getans(i+1,n,1,m,grid);
           ans=min(ans,x1+x2+x3);
       }
   }
    return ans;
    }
};
