class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
         const int n = grid.size(), m = grid[0].size();
        int ans=INT_MAX;
        auto findArea=[&](int x1,int y1,int x2,int y2){
            int r1=n,r2=-1,c1=m,c2=-1;
            for(int i=x1;i<=x2;i++){
                for(int j=y1;j<=y2;j++){
                    if(grid[i][j]==1){
                        r1=min(r1,i);
                        r2=max(r2,i);
                        c1=min(c1,j);
                        c2=max(c2,j);
                    }
                }
            }
            if(r1==n) return 0;
            int area=(r2-r1+1)*(c2-c1+1);
            return area;
        };
        for(int i=0;i<n;i++){
          for(int j=0;j<m;j++){
             int r1=findArea(0,0,i,j);
             {
                int r2=findArea(0,j+1,i,m-1);
                int r3=findArea(i+1,0,n-1,m-1);
                ans=min(ans,r1+r2+r3);
             }
             {
                int r2=findArea(0,j+1,n-1,m-1);
                int r3=findArea(i+1,0,n-1,j);
                ans=min(ans,r1+r2+r3);
             }
          }
        }
        for(int i=n-1;i>=0;i--){
          for(int j=m-1;j>=0;j--){
             int r1=findArea(i,j,n-1,m-1);
             {
                int r2=findArea(0,0,i-1,m-1);
                int r3=findArea(i,0,n-1,j-1);
                ans=min(ans,r1+r2+r3);
             }
             {
                int r2=findArea(0,0,n-1,j-1);
                int r3=findArea(0,j,i-1,m-1);
                ans=min(ans,r1+r2+r3);
             }
          }
        }
        for(int i1=0;i1<n;i1++){
          int r1=findArea(0,0,i1,m-1);
          for(int i2=i1+1;i2<n;i2++){
             int r2=findArea(i1+1,0,i2,m-1);
             int r3=findArea(i2+1,0,n-1,m-1);
             ans=min(ans,r1+r2+r3);
          }
        }
        for(int j1=0;j1<m;j1++){
          int r1=findArea(0,0,n-1,j1);
          for(int j2=j1+1;j2<m;j2++){
             int r2=findArea(0,j1+1,n-1,j2);
             int r3=findArea(0,j2+1,n-1,m-1);
             ans=min(ans,r1+r2+r3);
          }
        }
        return ans;
    }
};
