class Solution {
    int calculate(int[][] grid, int i1, int j1, int i2, int j2){
        int c1=1005,c2=0,d1=1005,d2=0;
        for(int i=i1;i<=i2;i++){
            for(int j=j1;j<=j2;j++){
                if(grid[i][j]==1){
                    c1=Math.min(c1,j);
                    c2=Math.max(c2,j);
                    d1=Math.min(d1,i);
                    d2=Math.max(d2,i);
                }
            }
        }
        if(c1==1005||d1==1005) return 0;
        return (d2-d1+1)*(c2-c1+1);
    }
    public int minimumSum(int[][] grid) {
        int n=grid.length;
        int m=grid[0].length;
        int res=(int)1e9;
        for(int i=0;i<n-1;i++){
            //case 2
            for(int j=0;j<m-1;j++){
                int h1=calculate(grid,0,0,i,j);
                int h2=calculate(grid,0,j+1,i,m-1);
                int h3=calculate(grid,i+1,0,n-1,m-1);
                // System.out.println(i+" "+j+" "+h1+" "+h2+" "+h3);
                if(h1==0||h2==0||h3==0) continue;
                res=Math.min(res,h1+h2+h3);
            }
            //case 0
            for(int j=0;j<m-1;j++){
                int h1=calculate(grid,0,0,i,m-1);
                int h2=calculate(grid,i+1,0,n-1,j);
                int h3=calculate(grid,i+1,j+1,n-1,m-1);
                // System.out.println(i+" "+j+" "+h1+" "+h2+" "+h3);
                if(h1==0||h2==0||h3==0) continue;
                res=Math.min(res,h1+h2+h3);
            }
            //case 1
            if(i<n-2){
                for(int j=0;j<n-1;j++){
                    int h1=calculate(grid,0,0,i,m-1);
                    int h2=calculate(grid,i+1,0,j,m-1);
                    int h3=calculate(grid,j+1,0,n-1,m-1);
                    // System.out.println(i+" "+j+" "+h1+" "+h2+" "+h3);
                    if(h1==0||h2==0||h3==0) continue;
                    res=Math.min(res,h1+h2+h3);
                }
            }
        }
        for(int i=0;i<m-1;i++){
            //case 4
            for(int j=0;j<n-1;j++){
                int h1=calculate(grid,0,0,j,i);
                int h2=calculate(grid,j+1,0,n-1,i);
                int h3=calculate(grid,0,i+1,n-1,m-1);
                if(h1==0||h2==0||h3==0) continue;
                res=Math.min(res,h1+h2+h3);
            }
            //case 3
            if(i<m-2){
                for(int j=i+1;j<m-1;j++){
                    int h1=calculate(grid,0,0,n-1,i);
                    int h2=calculate(grid,0,i+1,n-1,j);
                    int h3=calculate(grid,0,j+1,n-1,m-1);
                    if(h1==0||h2==0||h3==0) continue;
                    res=Math.min(res,h1+h2+h3);
                }
            }
            //case 5
            for(int j=0;j<n-1;j++){
                int h1=calculate(grid,0,0,n-1,i);
                int h2=calculate(grid,0,i+1,j,m-1);
                int h3=calculate(grid,j+1,i+1,n-1,m-1);
                if(h1==0||h2==0||h3==0) continue;
                res=Math.min(res,h1+h2+h3);
            }
        }
        return res;
    }
}
