class Solution {
public:

    int minimumArea(vector<vector<int>>& grid, int amnx, int amny, int amxx, int amxy) {
        int mnx = 1000, mny = 1000, mxx = 0, mxy = 0;
        for (int i = amnx; i < amxx; i ++){
            for (int j = amny; j < amxy; j ++){
                if (grid[i][j] == 1){
                    mnx = min(mnx, i);
                    mny = min(mny, j);
                    mxx = max(mxx, i);
                    mxy = max(mxy, j);
                }
            }
        }
        return (mxx - mnx + 1) * (mxy - mny + 1);
    }

    int minimumSum(vector<vector<int>>& grid) {
        int ans = 900;
        int w = grid.size(), h = grid[0].size();
        for (int i = 1; i < w; i ++){
            for (int j = i + 1; j < w; j ++){
                ans = min(ans, minimumArea(grid, 0, 0, i, h) + minimumArea(grid, i, 0, j, h) + minimumArea(grid, j, 0, w, h));
            }
        }
        for (int i = 1; i < h; i ++){
            for (int j = i + 1; j < h; j ++){
                ans = min(ans, minimumArea(grid, 0, 0, w, i) + minimumArea(grid, 0, i, w, j) + minimumArea(grid, 0, j, w, h));
            }
        }       
        for (int i = 1; i < w; i ++){
            for (int j = 1; j < h; j ++){
                ans = min(ans, minimumArea(grid, 0, 0, i, j) + minimumArea(grid, i, 0, w, j) + minimumArea(grid, 0, j, w, h));    
                ans = min(ans, minimumArea(grid, 0, 0, i, h) + minimumArea(grid, i, 0, w, j) + minimumArea(grid, i, j, w, h));    
                ans = min(ans, minimumArea(grid, 0, 0, w, j) + minimumArea(grid, 0, j, i, h) + minimumArea(grid, i, j, w, h));    
                ans = min(ans, minimumArea(grid, 0, 0, i, j) + minimumArea(grid, 0, j, i, h) + minimumArea(grid, i, 0, w, h));    
            }
        }
        return ans;
    }
};
