class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        auto cal=[&](int x, int y, int X, int Y)->int{
            int i1 = -1, i2 = 40, j1 = -1, j2 = 40;
            for (int i=x; i<=X; i++){
                for (int j=y; j<=Y; j++){
                    if (grid[i][j]==0)continue;
                    i1 = max(i1, i);
                    i2 = min(i2, i);
                    j1 = max(j1, j);
                    j2 = min(j2, j);
                }
            }
            int ans = (i1-i2+1)*(j1-j2+1);
            if (i1==-1){
                ans = 0;
            }
            return ans;
        };

        int ret = 1000;
        for (int i=0; i<grid[0].size(); i++){
            int ans = cal(0, 0, grid.size()-1, i-1);
            for (int k=i; k<grid[0].size(); k++){
                ret = min(ret, ans+cal(0, i, grid.size()-1, k-1)+cal(0, k, grid.size()-1, grid[0].size()-1));
                // if (ret==40){
                //     cout<<i<<" "<<k<<"  jp\n";
                // }
            }
            for (int j=0; j<grid.size(); j++){
                ret = min(ret, ans+cal(0, i, j-1, grid[0].size()-1)+cal(j, i, grid.size()-1, grid[0].size()-1));
                // if (ret==40){
                //     cout<<i<<" "<<j<<"  jp1\n";
                // }
            }
            ans = cal(0, i, grid.size()-1, grid[0].size()-1);
            for (int j=0; j<i; j++){
                ret = min(ret, ans+cal(0, 0, grid.size()-1, j-1)+cal(0, j, grid.size()-1, i-1));
                // if (ret==40){
                //     cout<<i<<" "<<j<<"  jp2\n";
                // }
            }
            for (int j=0; j<grid.size(); j++){
                ret = min(ret, ans+cal(0, 0, j-1, i-1)+cal(j, 0, grid.size()-1, i-1));
                // if (ret==40){
                //     cout<<i<<" "<<j<<"  jp3\n";
                // }
            }
        }
        for (int i=0; i<grid.size(); i++){
            int ans = cal(0, 0, i-1, grid[0].size()-1);
            for (int k=i; k<grid.size(); k++){
                ret = min(ret, ans+cal(i, 0, k-1, grid[0].size()-1)+cal(k, 0, grid.size()-1, grid[0].size()-1));
                // if (ret==40){
                //     cout<<i<<" "<<k<<"  ip\n";
                // }
            }
            for (int j=0; j<grid[0].size(); j++){
                ret = min(ret, ans+cal(i, 0, grid.size()-1, j-1)+cal(i, j, grid.size()-1, grid[0].size()-1));
                // if (ret==40){
                //     cout<<i<<" "<<j<<"  ip1\n";
                // }
            }
            ans = cal(i, 0, grid.size()-1, grid[0].size()-1);
            // for (int j=0; j<i; j++){
            //     ret = min(ret, ans+cal(0, 0, grid.size()-1, j-1)+cal(0, j, grid.size()-1, i-1));
            // }
            for (int j=0; j<grid[0].size(); j++){
                ret = min(ret, ans+cal(0, 0, i-1, j-1)+cal(0, j, i-1, grid[0].size()-1));
                // if (ret==40){
                //     cout<<i<<" "<<j<<"  ip2\n";
                // }
            }
        }
        return ret;
    }
};
