class Solution {
    int area(vector<vector<int>>& a, int i1, int i2, int j1, int j2) {
        int min_i = i2 + 1, min_j = j2 + 1, max_i = i1, max_j = j1;
        for (int i = i1; i <= i2; i++)
            for (int j = j1; j <= j2; j++)
                if (a[i][j]) {
                    min_i = min(min_i, i);
                    min_j = min(min_j, j);
                    max_i = max(max_i, i);
                    max_j = max(max_j, j);
                }
        if (min_i <= max_i)
            return (max_i - min_i + 1) * (max_j - min_j + 1);
        else
            return 0;
    }

public:
    int minimumSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ret = n * m;
        for (int i = 0; i + 2 < n; i++)
            for (int j = i + 1; j + 1 < n; j++)
                ret = min(ret, area(a, 0, i, 0, m - 1) + area(a, i + 1, j, 0, m - 1) + area(a, j + 1, n - 1, 0, m - 1));
        for (int i = 0; i + 2 < m; i++)
            for (int j = i + 1; j + 1 < m; j++)
                ret = min(ret, area(a, 0, n - 1, 0, i) + area(a, 0, n - 1, i + 1, j) + area(a, 0, n - 1, j + 1, m - 1));
        for (int i = 0; i + 1 < n; i++)
            for (int j = 0; j + 1 < m; j++) {
                ret = min(ret, area(a, 0, i, 0, j) + area(a, 0, i, j + 1, m - 1) + area(a, i + 1, n - 1, 0, m - 1));
                ret = min(ret, area(a, i + 1, n - 1, 0, j) + area(a, i + 1, n - 1, j + 1, m - 1) + area(a, 0, i, 0, m - 1));
                ret = min(ret, area(a, 0, i, 0, j) + area(a, i + 1, n - 1, 0, j) + area(a, 0, n - 1, j + 1, m - 1));
                ret = min(ret, area(a, 0, i, j + 1, m - 1) + area(a, i + 1, n - 1, j + 1, m - 1) + area(a, 0, n - 1, 0, j));
            }
        return ret;
    }
};
