func minimumSum(grid [][]int) int {
    ans:=math.MaxInt
    m,n:=len(grid),len(grid[0])
    for i:=0;i<m-1;i++{
        blockUp:=minimumArea(grid,0,n-1,0,i)
        if blockUp!=0{
            ans=min(ans,blockUp+biArea(grid,0,n-1,i+1,m-1))
        }

        blockDown:=minimumArea(grid,0,n-1,i+1,m-1)
        if blockDown!=0{
            ans=min(ans,blockDown+biArea(grid,0,n-1,0,i))
        }
    }
    for j:=0;j<n-1;j++{
        blockLeft:=minimumArea(grid,0,j,0,m-1)
        if blockLeft!=0{
            ans=min(ans,blockLeft+biArea(grid,j+1,n-1,0,m-1))
        }
        blockRight:=minimumArea(grid,j+1,n-1,0,m-1)
        if blockRight!=0{
            ans=min(ans,blockRight+biArea(grid,0,j,0,m-1))
        }
    }
    return ans
}
func biArea(grid [][]int,left,right,up,down int)int{
    ans:=math.MaxInt
    for i:=up;i<down;i++{
        blockUp:=minimumArea(grid,left,right,up,i)
        if blockUp==0{
            continue
        }

        blockDown:=minimumArea(grid,left,right,i+1,down)
        if blockDown==0{
            continue
        }
        ans=min(ans,blockUp+blockDown)
    }
    for j:=left;j<right;j++{
        blockLeft:=minimumArea(grid,left,j,up,down)
        if blockLeft==0{
            continue
        }
        blockRight:=minimumArea(grid,j+1,right,up,down)
        if blockRight==0{
            continue
        }
        ans=min(ans,blockLeft+blockRight)
    }
    return ans
}
func minimumArea(grid [][]int,left,right,up,down int) int {
    m,n:=len(grid),len(grid[0])
    minX,maxX,minY,maxY:=m,0,n,0
    for i:=up;i<=down;i++{
        for j:=left;j<=right;j++{
            if grid[i][j]==1{
                minX=min(minX,i)
                maxX=max(maxX,i)
                minY=min(minY,j)
                maxY=max(maxY,j)
            }
        }
    }
    ans:=(maxX-minX+1)*(maxY-minY+1)
    if ans<0{
        return 0
    }
    return ans
}
