class Solution {
public:
    int minimumSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        auto f1 = [&](int x1, int y1, int x2, int y2) {
            int mnx = 1e9, mxx = -1, mny = 1e9, mxy = -1;
            for (int i = x1; i <= x2; i++) {
                for (int j = y1; j <= y2; j++) {
                    if (a[i][j] == 1) {
                        mnx = min(mnx, i);
                        mxx = max(mxx, i);
                        mny = min(mny, j);
                        mxy = max(mxy, j);
                    }
                }
            }
            if (mxx == -1) return 1;
            return (mxx - mnx + 1) * (mxy - mny + 1);
        };
        auto f2 = [&](int x1, int y1, int x2, int y2) {
            int ans = 1e9;
            for (int i = x1; i < x2; i++) {
                ans = min(ans, f1(x1, y1, i, y2) + f1(i + 1, y1, x2, y2));
            }
            for (int i = y1; i < y2; i++) {
                ans = min(ans, f1(x1, y1, x2, i) + f1(x1, i + 1, x2, y2));
            }

            return ans;
        };
        int ans = 1e9;
        for (int i = 0; i < n; i++) {
            ans = min(ans, f1(0, 0, i, m - 1) + f2(i + 1, 0, n - 1, m - 1));
            ans = min(ans, f2(0, 0, i, m - 1) + f1(i + 1, 0, n - 1, m - 1));
        }
        for (int i = 0; i < m; i++) {
            ans = min(ans, f1(0, 0, n - 1, i) + f2(0, i + 1, n - 1, m - 1));
            ans = min(ans, f2(0, 0, n - 1, i) + f1(0, i + 1, n - 1, m - 1));
        }
        return ans;
    }
};
