class Solution {
public:

int minimumArea2(vector<vector<int>>& grid,int row1, int row2, int col1, int col2) {
    int a = 1e9, b = 1e9, c = -1e9, d = -1e9;
    bool val = false;
    for (int i = row1; i<=row2; i++) {
        for (int j = col1; j <=col2; j++) {
            if (grid[i][j] == 1)
            {
                a = min(a, i), b = min(b, j);
                c = max(c, i), d = max(d, j);
                val = true;
            }
        }
    }
    if (val)
        return (c - a + 1) * (d - b + 1);
    else
        return 0;
}


int minimumSum(vector<vector<int>>& grid) {
    //横着3个，竖着3个，（上边1个，下边2个），（上边2个，下边1个），（左边2个，右边1个），（左边1个，右边2个）
    int res = 1e9;
    int row = grid.size(), col = grid[0].size();

    //横着3个
    for (int i = 0; i < row; i++) {
        for (int j = i + 2; j < row; j++) {
            res = min(res, minimumArea2(grid, 0, i, 0, col - 1) + minimumArea2(grid, i + 1, j - 1, 0, col - 1) + minimumArea2(grid, j, row - 1, 0, col - 1));
        }
    }

    //竖着3个
    for (int i = 0; i < col; i++) {
        for (int j = i + 2; j < col; j++) {
            res = min(res, minimumArea2(grid, 0, row-1, 0, i) + minimumArea2(grid, 0, row - 1, i+1, j- 1) + minimumArea2(grid, 0, row - 1, j, col-1));
        }
    }

    //（上边1个，下边2个）
    for (int i = 0; i < row-1; i++) {
        for (int j = 0; j < col-1; j++) {
            res = min(res, minimumArea2(grid, 0, i, 0, col - 1) + minimumArea2(grid, i+1, row - 1, 0, j) + minimumArea2(grid, i+1, row - 1, j+1, col - 1));
        }
    }

    //（上边2个，下边1个）
    for (int i = 0; i < row - 1; i++) {
        for (int j = 0; j < col - 1; j++) {
            res = min(res, minimumArea2(grid, 0, i, 0, j) + minimumArea2(grid, 0,i,j+1,col-1) + minimumArea2(grid,i+1,row-1,0,col-1));
        }
    }


    //（左边2个，右边1个）
    for (int i = 0; i < row - 1; i++) {
        for (int j = 0; j < col - 1; j++) {
            res = min(res, minimumArea2(grid, 0, i, 0, j) + minimumArea2(grid, i+1,row-1,0,j) + minimumArea2(grid, 0, row - 1, j+1, col - 1));
        }
    }

    //（左边1个，右边2个）
    for (int i = 0; i < row - 1; i++) {
        for (int j = 0; j < col - 1; j++) {
            res = min(res, minimumArea2(grid, 0, row-1, 0, j) + minimumArea2(grid, 0, i, j+1, col-1) + minimumArea2(grid, i+1, row - 1, j + 1, col - 1));
        }
    }
    return res;
}
};
