class Solution {
public:
    int minimumSum(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        auto get_area = [&](int sa, int sb, int ta, int tb) -> int{
            int sx = -1, sy = -1, tx = 1e9, ty = 1e9;
            for(int i = sa; i <= ta; i++){
                for(int j = sb; j <= tb; j++){
                    if(g[i][j]){
                        sx = max(sx, i), tx = min(tx, i);
                        sy = max(sy, j), ty = min(ty, j);
                    }
                }
            }
            if(sx == -1 || tx == 1e9){
                return 0;
            }
            return (sy - ty + 1)*(sx - tx + 1);
        };
        int min_area = 100000000;
        for(int i = 0; (i+1) < n; i++){
            for(int j = 0; (j+1) < m; j++){
                int cur_area = get_area(0, 0, i, j);
                if(j < m && i < n){
                    min_area = min(min_area, cur_area + get_area(0, j+1, i, m-1) + get_area(i+1, 0, n-1, m-1));
                    min_area = min(min_area, cur_area + get_area(0, j+1, n-1, m-1) + get_area(i+1, 0, n-1, j));
                }
            }
        }
        for(int i = 1; i < n; i++){
            for(int j = 1; j < m; j++){
                int cur_area = get_area(i, j, n-1, m-1);
                min_area = min(min_area, cur_area + get_area(0, j, i-1, m-1) + get_area(0, 0, n-1, j-1));
                min_area = min(min_area, cur_area + get_area(i, 0, n-1, j-1) + get_area(0, 0, i-1, m-1));

            }
        }
        cout<<min_area<<'\n';
        for(int i = 0; i < n; i++){
            int cur_area = get_area(0, 0, i, m-1);
            for(int p = i+1; (p+1) < n; p++){
                min_area = min(min_area, cur_area + get_area(i+1, 0, p, m-1) + get_area(p+1, 0, n-1, m-1));
            }
        }
        cout<<min_area<<'\n';
        for(int j = 0; j < m; j++){
            int cur_area = get_area(0, 0, n-1, j);
            for(int p = j+1; (p+1) < m; p++){
                min_area = min(min_area, cur_area + get_area(0, j+1, n-1, p) + get_area(0, p+1, n-1, m-1));
            }
        }

        return min_area;
    }
};
