class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int m=grid.size(), n=grid[0].size();

        long long dp[31][31][31][31][3]={};
        for(int i=0;i<m;++i){
            for(int j=0;j<n;++j){
                for(int ii=i;ii<m;++ii){
                    int ax=INT_MAX, bx=INT_MIN;
                    int ay=INT_MAX, by=INT_MIN;
                    int st=0;
                    for(int jj=j;jj<n;++jj){
                        for(int k=i;k<=ii;++k){
                            if(grid[k][jj]){
                                st=1;
                                ax=min(ax, k);
                                bx=max(bx, k);
                                ay=min(ay, jj);
                                by=max(by, jj);
                            }
                        }
                        if(st){
                            dp[i][j][ii][jj][0]=(bx-ax+1)*(by-ay+1);
                        }
                        else{
                            dp[i][j][ii][jj][0]=0;
                        }
                    }
                }
            }
        }
        for(int cnt=1;cnt<=2;++cnt){

            for(int i=0;i<m;++i){
                for(int j=0;j<n;++j){
                    for(int ii=i;ii<m;++ii){
                        for(int jj=j;jj<n;++jj){
                            dp[i][j][ii][jj][cnt]=INT_MAX;
                            for(int k=i;k+1<=ii;++k){
                                dp[i][j][ii][jj][cnt] = min(dp[i][j][ii][jj][cnt], dp[i][j][k][jj][0]+dp[k+1][j][ii][jj][cnt-1]);
                            }
                            for(int k=i;k+1<=ii;++k){
                                dp[i][j][ii][jj][cnt] = min(dp[i][j][ii][jj][cnt], dp[i][j][k][jj][cnt-1]+dp[k+1][j][ii][jj][0]);
                            }
                            for(int k=j;k+1<=jj;++k){
                                dp[i][j][ii][jj][cnt] = min(dp[i][j][ii][jj][cnt], dp[i][j][ii][k][0]+dp[i][k+1][ii][jj][cnt-1]);
                            }
                            for(int k=j;k+1<=jj;++k){
                                dp[i][j][ii][jj][cnt] = min(dp[i][j][ii][jj][cnt], dp[i][j][ii][k][cnt-1]+dp[i][k+1][ii][jj][0]);
                            }
                        }
                    }
                }
            }

        }
        // int a=0,b=0;
        // for(int i=a;i<m;++i){
        //     for(int j=b;j<n;++j){
        //         cout<<dp[a][b][i][j][1]<<" ";
        //     }
        //     cout<<endl;
        // }
        return dp[0][0][m-1][n-1][2];
    }
};
