    int minimumArea1(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        int lx=n,rx=0,ly=m,ry=0;
        For(i,0,n-1)
            For(j,0,m-1)
                if(a[i][j])lx=min(lx,i),rx=max(rx,i),ly=min(ly,j),ry=max(ry,j);

        return (rx-lx+1)*(ry-ly+1);
    }
    int minimumArea2(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size(),ans=n*m;
        vector<vector<int>>b,c,g;c=a;
        reverse(c.begin(),c.end());
        For(I,0,n-2){
            int res=0;
            b.push_back(a[I]);
            c.pop_back();
            res+=minimumArea1(b);
            res+=minimumArea1(c);
            ans=min(ans,res);
        }
        vector<vector<int>>(m,vector<int>(n)).swap(g);
        For(i,0,n-1)For(j,0,m-1)g[j][i]=a[i][j];
        vector<vector<int>>().swap(b);
        c=g;
        reverse(c.begin(),c.end());
        For(I,0,m-2){
            int res=0;
            b.push_back(g[I]);
            c.pop_back();
            res+=minimumArea1(b);
            res+=minimumArea1(c);
            ans=min(ans,res);
        }
        return ans;
    }
