class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        const int inf = 1e9+3;

        auto f = [&](int u, int l, int d, int r) -> int {
            int mni = inf, mxi = -inf, mnj = inf, mxj = -inf;
            for (int i=u; i<=d; i++) {
                for (int j=l; j<=r; j++) {
                    if (grid[i][j]) {
                        mni = min(mni, i);
                        mxi = max(mxi, i);
                        mnj = min(mnj, j);
                        mxj = max(mxj, j);
                    }
                }
            }
            if (mni == inf) return -1;
            return (mxi - mni + 1) * (mxj - mnj + 1);
        };

        int ans = 1e9+3;

        for (int i=1; i<n; i++) {
            for (int j=1; j<m; j++) {
                int a, b, c;

                a = f(0, 0, i-1, j-1), b = f(i, 0, n-1, j-1), c = f(0, j, n-1, m-1);
                if (a != -1 && b != -1 && c != -1) ans = min(ans, a + b + c);

                a = f(0, 0, i-1, j-1), b = f(0, j, i-1, m-1), c = f(i, 0, n-1, m-1);
                if (a != -1 && b != -1 && c != -1) ans = min(ans, a + b + c);

                a = f(0, 0, n-1, j-1), b = f(0, j, i-1, m-1), c = f(i, j, n-1, m-1);
                if (a != -1 && b != -1 && c != -1) ans = min(ans, a + b + c);

                a = f(0, 0, i-1, m-1), b = f(i, 0, n-1, j-1), c = f(i, j, n-1, m-1);
                if (a != -1 && b != -1 && c != -1) ans = min(ans, a + b + c);
            }
        }

        for (int i1=1; i1<n; i1++) {
            for (int i2=i1+1; i2<n; i2++) {
                int a = f(0, 0, i1-1, m-1), b = f(i1, 0, i2-1, m-1), c = f(i2, 0, n-1, m-1);
                if (a != -1 && b != -1 && c != -1) ans = min(ans, a + b + c);
            }
        }

        for (int j1=1; j1<m; j1++) {
            for (int j2=j1+1; j2<m; j2++) {
                int a = f(0, 0, n-1, j1-1), b = f(0, j1, n-1, j2-1), c = f(0, j2, n-1, m-1);
                if (a != -1 && b != -1 && c != -1) ans = min(ans, a + b + c);
            }
        }

        return ans;
    }
};
