int qs[31][31];

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        memset(qs, 0, sizeof qs);
        int nr = grid.size(), nc = grid[0].size();
        for (int i = 0; i < nr; ++i) {
            for (int j = 0; j < nc; ++j) {
                qs[i +1][j +1] = grid[i][j];
            }
        }
        int tot = qs[nr][nc];
        // case T
        int mi = nr * nc;
        auto calc = [&](int lrow, int lcol, int rrow, int rcol) -> int {
            int lm = 1001, rm = -1, tm = 1001, bm = -1;
            for (int i = lrow; i <= rrow; ++i) {
                for (int j = lcol; j <= rcol; ++j) {
                    if (qs[i][j]) {
                        lm = min(lm, j), rm = max(rm, j);
                        tm = min(tm, i), bm = max(bm, i);
                    }
                }
            }
            if (rm < lm) return 0;
            return (rm - lm + 1) * (bm - tm + 1);
        };
        auto calc2 = [&](int lrow, int lcol, int rrow, int rcol) -> int{
            int mi = nr * nc;
            if (lrow < rrow) {
                for (int i = lrow; i < rrow; ++i) {
                    mi = min(mi, calc(lrow, lcol, i, rcol) + calc(i + 1, lcol, rrow, rcol));
                }
            }
            if (lcol < rcol) {
                for (int i = lcol; i < rcol; ++i) {
                    mi = min(mi, calc(lrow, lcol, rrow, i) + calc(lrow, i + 1, rrow, rcol));
                }
            }
            return mi;
        };
        for (int i = 1; i < nr; ++i) {
            mi = min(mi, calc(1, 1, i, nc) + calc2(i + 1, 1, nr, nc));
            if (i > 1) {
                mi = min(mi, calc2(1, 1, i, nc) + calc(i + 1, 1, nr, nc));
            }
        }
        for (int i = 1; i < nc; ++i) {
            mi = min(mi, calc(1, 1, nr, i) + calc2(1, i + 1, nr, nc));
            if (i > 1) {
                mi = min(mi, calc2(1, 1, nr, i) + calc(1, i + 1, nr, nc));
            }
        }
        return mi;
    }
};
