class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        auto findRectangle = [&](int sx , int sy , int ex , int ey) {
            int mnx = 1001 , mny = 1001, mx = 0 , my = 0;
            bool ok = false;
            for(int i = sx;i<=ex;++i) {
                for(int j = sy;j<=ey;++j) {
                    if(grid[i][j]) {
                        mnx = min(i , mnx);
                        mx = max(i , mx);
                        mny = min(j , mny);
                        my = max(j , my);
                        ok = true;
                    }
                }
            }  
            if(ok) return (mx - mnx + 1) * (my - mny + 1);
            return 0;
        };
        int n = grid.size() , m = grid[0].size();
        int ans = n * m + 1;
        for(int i = 0;i<n;++i) {
            for(int j = i + 1;j<n;++j){
                ans = min(ans , findRectangle(0,0,i,m-1) + findRectangle(i+1,0,j,m-1) + findRectangle(j+1,0,n-1,m-1));
            }
        }
        for(int i = 0;i<m;++i){
            for(int j = i + 1;j<m;++j) {
                ans = min(ans , findRectangle(0,0,n-1,i) + findRectangle(0,i+1,n-1,j) + findRectangle(0,j+1,n-1,m-1));
            }
        }
        for(int i = 0;i<n-1;++i) {
            for(int j = 0;j<m-1;++j) {
                ans = min(ans , findRectangle(0,0,i,m-1) + findRectangle(i+1,0,n-1,j) + findRectangle(i+1,j+1,n-1,m-1));
                int ri = n - i - 1;
                ans = min(ans , findRectangle(ri,0,n-1,m-1) + findRectangle(0,0,ri-1,j) + findRectangle(0,j+1,ri-1,m-1));
            }
        }

        for(int j = 0;j<m-1;++j) {
            for(int i = 0;i<n-1;++i) {
                ans = min(ans , findRectangle(0,0,n-1,j) + findRectangle(0,j+1,i,m-1) + findRectangle(i+1,j+1,n-1,m-1));
                int rj = m - j - 1;
                ans = min(ans , findRectangle(0,rj,n-1,m-1) + findRectangle(0,0,i,rj-1) + findRectangle(i+1,0,n-1,rj-1));
            }
        }
        for(int i = 0;i<n-1;++i){
            for(int j = 0;j<m-1;++j) {
                ans = min(ans , findRectangle(0,0,i,j) + findRectangle(i+1,0,n-1,j) + findRectangle(0,j+1,n-1,m-1));
                // cout << 0 << ' ' << 0 << ' ' << i << ' ' << j << endl;
                // cout << i + 1 << ' ' << 0 << ' ' << n - 1 << ' ' << j << endl;
                // cout << 0 << ' ' << j + 1 << ' ' << n - 1 << ' ' << m - 1 << endl;
                // cout << findRectangle(0,0,i,j) + findRectangle(i+1,0,n-1,j) + findRectangle(0,j+1,n-1,m-1) << endl;
                // cout << endl;

                ans = min(ans , findRectangle(0,0,i,j) + findRectangle(i+1,0,n-1,m-1) + findRectangle(0,j+1,i,m-1));
            }
        }
        return ans;
    }
};
