class Solution {
public:
    int fin(int dx,int dy,int ex,int ey,vector<vector<int>>&grid){
        int mix=1000000,miy=1000000,maxx=-1,may=-1;
        for(int i=dx;i<=ex;i++){
            for(int j=dy;j<=ey;j++){
                if(grid[i][j]==1){
                    mix=min(mix,i);
                    miy=min(miy,j);
                    maxx=max(maxx,i);
                    may=max(may,j);
                }
            }
        }
        if(mix==1000000)return 0;
        return (maxx-mix+1)*(may-miy+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int ans=1e9;
        int n=grid.size();
        int m=grid[0].size();
        //cout<<grid[1][2]<<endl;
        for(int i=0;i+1<grid.size();i++){
            for(int j=0;j+1<m;j++){
                int t1=fin(0,0,i,j,grid);

                if(!t1)continue;
                int t2=fin(i+1,0,n-1,j,grid);
                int t3=fin(0,j+1,n-1,m-1,grid);
                if(t2&&t3)ans=min(ans,t1+t2+t3);
                int t4=fin(i+1,0,n-1,m-1,grid);
                int t5=fin(0,j+1,i,m-1,grid);
                if(t4&&t5)ans=min(ans,t1+t4+t5);
                //if(i==1&&j==2)cout<<t2<<" "<<t3<<endl;
            }
        }
        for(int i=0;i+2<n;i++){
            int t1=fin(0,0,i,m-1,grid);
            if(!t1)continue;
            for(int j=i+1;j+1<n;j++){
                int t2=fin(i+1,0,j,m-1,grid);
                if(!t2)continue;
                int t3=fin(j+1,0,n-1,m-1,grid);
                if(!t3)continue;
                ans=min(ans,t1+t2+t3);
            }

        }
        for(int i=0;i+1<n;i++){
            int t1=fin(0,0,i,m-1,grid);
            if(!t1)continue;
            for(int j=0;j+1<m;j++){
                int t2=fin(i+1,0,n-1,j,grid);
                if(!t2)continue;
                int t3=fin(i+1,j+1,n-1,m-1,grid);
                if(!t3)continue;
                ans=min(ans,t1+t2+t3);
            }
        }
        for(int i=0;i+2<m;i++){
            int t1=fin(0,0,n-1,i,grid);
            if(!t1)continue;
            for(int j=i+1;j+1<m;j++){
                int t2=fin(0,i+1,n-1,j,grid);
                if(!t2)continue;
                int t3=fin(0,j+1,n-1,m-1,grid);
                if(!t3)continue;
                ans=min(ans,t1+t2+t3);
            }

        }
        for(int i=0;i+1<m;i++){
            int t1=fin(0,0,n-1,i,grid);
            if(!t1)continue;
             for(int j=0;j+1<n;j++){
                int t2=fin(0,i+1,j,m-1,grid);
                if(!t2)continue;
                int t3=fin(j+1,i+1,n-1,m-1,grid);
                if(!t3)continue;
                ans=min(ans,t1+t2+t3);
            }
        }
        return ans;
    }
};
