class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def f2(r1,c1,r2,c2):
            c = d = 0
            a = b = inf
            for i in range(r1,r2+1):
                for j in range(c1,c2+1):
                    x = grid[i][j]
                    if x == 1:
                        a = min(a,i)
                        c = max(c,i)
                        b = min(b,j)
                        d = max(d,j)
            return (d - b + 1) * (c - a + 1)
        @cache
        def split(r1,c1,r2,c2,cnt):
            if cnt == 0:
                return f2(r1,c1,r2,c2)
            ret = f2(r1,c1,r2,c2)
            for i in range(r1,r2):
                for k in range(cnt):
                    ret = min(ret,split(r1,c1,i,c2,k) + split(i+1,c1,r2,c2,cnt-1-k))
            for j in range(c1,c2):
                for k in range(cnt):
                    ret = min(ret,split(r1,c1,r2,j,k) + split(r1,j+1,r2,c2,cnt-1-k))
            return ret

        return split(0,0,len(grid)-1,len(grid[0])-1,2)
