class Solution {
public:
    int count(vector<vector<int>>& grid, int lx, int rx, int ly, int ry) {
        int rX = -1, lX = 40, rY = -1, lY = 40;
        for(int k=lx;k<=rx;k++) {
            for(int i=ly;i<=ry;i++) {
                if (grid[k][i]) {
                    rX = max(rX, k);
                    lX = min(lX, k);
                    rY = max(rY, i);
                    lY = min(lY, i);
                }
            }
        }
        if (rX == -1) return 0;
        return (rX - lX + 1) * (rY - lY + 1);
    } 
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 1000;
        for(int p=0;p<n;p++) {
            for(int q=p+1;q<n;q++) {
                int tmp = count(grid, 0, p, 0, m - 1) + count(grid, p + 1, q, 0, m - 1) + count(grid, q + 1, n - 1, 0, m - 1);
                ans = min(ans, tmp);
            }
        }
        for(int p=0;p<n;p++) {
            for(int q=0;q<m;q++) {
                int tmp = count(grid, 0, p, 0, m - 1) + count(grid, p + 1, n - 1, 0, q) + count(grid, p + 1, n - 1, q + 1, m - 1);
                ans = min(ans, tmp);
                tmp = count(grid, p, n - 1, 0, m - 1) + count(grid, 0, p - 1, 0, q) + count(grid, 0, p - 1, q + 1, m - 1);
                ans = min(ans, tmp);
            }
        }
        for(int p=0;p<m;p++) {
            for(int q=p+1;q<m;q++) {
                int tmp = count(grid, 0, n - 1, 0, p) + count(grid, 0, n - 1, p + 1, q) + count(grid, 0, n - 1, q + 1, m - 1);
                ans = min(ans, tmp);
            }
        }
        for(int p=0;p<m;p++) {
            for(int q=0;q<n;q++) {
                int tmp = count(grid, 0, n - 1, 0, p) + count(grid, 0, q, p + 1, m - 1) + count(grid, q + 1, n - 1, p + 1, m - 1);
                ans = min(ans, tmp);
                tmp = count(grid, 0, n - 1, p, m - 1) + count(grid, 0, q, 0, p - 1) + count(grid, q + 1, n - 1, 0, p - 1);
                ans = min(ans, tmp);
            }
        }
        return ans;
    }
};
