class Solution {
public:
    vector<vector<int>>grid;
    int solve(int rs,int re,int cs,int ce){
        // cout<<rs<<re<<cs<<ce<<endl;
        for(int i=rs;i<=re;i++){
            int cnt=0;
            for(int j=cs;j<=ce;j++){
                if(grid[i][j]) cnt++;
            }
            if(cnt==0) rs++;
            else break;
        }
        for(int i=re;i>=rs;i--){
            int cnt=0;
            for(int j=cs;j<=ce;j++){
                if(grid[i][j]) cnt++;
            }
            if(cnt==0) re--;
            else break;
        }
        for(int i=cs;i<=ce;i++){
            int cnt=0;
            for(int j=rs;j<=re;j++){
                if(grid[j][i]) cnt++;
            }
            if(cnt==0) cs++;
            else break;
        }
        for(int i=ce;i>=cs;i--){
            int cnt=0;
            for(int j=rs;j<=re;j++){
                if(grid[j][i]) cnt++;
            }
            if(cnt==0) ce--;
            else break;
        }
        // cout<<rs<<re<<cs<<ce<<endl;
        return (ce-cs+1)*(re-rs+1);
    }
    int f(int rs,int re,int cs,int ce,int div){
        if(div==2) return solve(rs,re,cs,ce);
        int ans=1e9;
        for(int i=rs;i<re;i++){
            ans=min(ans,f(rs,i,cs,ce,div+1)+solve(i+1,re,cs,ce));
            // cout<<ans<<endl;
            ans=min(ans,solve(rs,i,cs,ce)+f(i+1,re,cs,ce,div+1));
            // cout<<ans<<endl;
        }
        for(int i=cs;i<ce;i++){
            ans=min(ans,f(rs,re,cs,i,div+1)+solve(rs,re,i+1,ce));
            // cout<<ans<<endl;
            ans=min(ans,solve(rs,re,cs,i)+f(rs,re,i+1,ce,div+1));
            // cout<<ans<<endl;
        }
        return ans;
    }
    int minimumSum(vector<vector<int>>& grid) {
        this->grid=grid;
        int m=grid.size(),n=grid[0].size();
        return f(0,m-1,0,n-1,0);
    }   
};
