class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {

             int n = grid.size() , m = grid[0].size() ;

            function<int(int , int , int  , int)>func = [&](int is , int ie , int js , int je) ->int{

             int a = 1e9 , b = 0 , c = 1e9 , d = 0 ;

              for(int i = is; i <= ie ; i ++){
                        for(int j = js ; j <= je ; j ++){
                                  if(grid[i][j]){
                                            a = min(a , i) ;
                                            b = max(b , i) ;
                                          c = min(c , j);
                                          d = max(d , j) ;
                                  }
                        }
              }

           if(a == 1e9) return 1e8 ;
             int ans = (b - a + 1) * (d - c + 1) ;
            return ans;
            } ;

             int ans = 1e8 ;

            for(int i = 0 ; i < n - 2 ; i ++){
                      for(int j = i + 1 ; j < n - 1; j ++){
                                ans = min(ans , func(0 , i , 0 , m - 1) + func(i + 1 , j , 0  , m - 1) + func(j + 1 , n - 1 , 0 , m - 1)) ;
                      }
            }
             for(int i = 0 ; i < m - 2 ; i ++){
                      for(int j = i + 1 ; j < m - 1; j ++){
                                ans = min(ans , func(0 ,n - 1, 0 , i) + func(0 , n - 1 , i + 1  , j) + func(0 , n - 1 , j + 1 , m - 1)) ;
                      }
            }

             for(int i = 0 ; i < n - 1 ; i ++){
                      for(int j =0 ; j < m - 1; j ++){
                                ans = min(ans , func(0 , i , 0 , m - 1) + func(i + 1 , n - 1 , 0  , j) + func(i + 1 , n - 1 , j + 1 , m - 1)) ;
                      }
            }
              for(int i = n - 1 ; i > 0 ; i --){
                      for(int j = 0 ; j < m - 1; j ++){
                                ans = min(ans , func(i , n - 1 , 0 , m - 1) + func(0 , i - 1 , 0  , j) + func(0 , i - 1 , j + 1 , m - 1)) ;
                      }
            }
                for(int i = 0 ; i < m - 1 ; i ++){
                      for(int j = 0 ; j < n - 1; j ++){
                                ans = min(ans , func(0 , n - 1 , 0 , i) + func(0 , j , i + 1 , m - 1) + func(j + 1 , n - 1 , i + 1 , m - 1)) ;
                      }
            }
                for(int i = m - 1 ; i > 0 ; i --){
                      for(int j = 0 ; j < n - 1; j ++){
                                ans = min(ans , func(0 , n - 1 , i , m - 1) + func(0 , j , 0 , i - 1) + func(j + 1 , n - 1 , 0, i - 1)) ;
                      }
            }

            return ans;

    }
};
