#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    int cal(vector<vector<int>>& g, int low, int high, int left, int right) {
        if (low > high || left > right) {
            return 0;
        }
        int n = high - low + 1, m = right - left + 1;
        int l = m, r = 0;
        int lo = n, hi = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (g[i + low][j + left]) {
                    l = min(j, l);
                    r = max(j, r);
                    lo = min(lo, i);
                    hi = max(hi, i);
                }
            }
        }
        return (r - l + 1) * (hi - lo + 1);
    }

    int cal2d(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = cal(g, 0, n - 1, 0, m - 1);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                int cur = cal(g, 0, i, 0, j) + cal(g, 0, n - 1, j + 1, m - 1) + cal(g, i + 1, n - 1, 0, j);
                ans = min(cur, ans);
                cur = cal(g, 0, i, 0, j) + cal(g, i + 1, n - 1, 0, m - 1) + cal(g, 0, i, j + 1, m - 1);
                ans = min(ans, cur);
                cur = cal(g, 0, i, 0, m - 1) + cal(g, i + 1, n - 1, 0, j) + cal(g, i + 1, n - 1, j + 1, m - 1);
                ans = min(ans, cur);
                cur = cal(g, 0, n - 1, 0, j) + cal(g, 0, i, j + 1, m - 1) + cal(g, i + 1, n - 1, j + 1, m - 1);
                ans = min(ans, cur);
            }
        }
        for (int i = 0; i < n; i++) {
            int cur = cal(g, 0, i, 0, m - 1);
            for (int j = i + 1; j < n; j++) {
                int tmp = cal(g, i + 1, j, 0, m - 1) + cal(g, j + 1, n - 1, 0, m - 1);
                ans = min(ans, tmp + cur);
            }
        }
        for (int i = 0; i < m; i++) {
            int cur = cal(g, 0, n - 1, 0, i);
            for (int j = i + 1; j < m; j++) {
                int tmp = cal(g, 0, n - 1, i + 1, j) + cal(g, 0, n - 1, j + 1, m - 1);
                ans = min(ans, tmp + cur);
            }
        }
        return ans;
    }

    int minimumSum(vector<vector<int>>& g) {
        vector<vector<int>> gl = g;
        reverse(gl.begin(), gl.end());
        vector<vector<int>> gu = g;
        for (auto& row : gu) {
            reverse(row.begin(), row.end());
        }
        vector<vector<int>> glu = gu;
        reverse(glu.begin(), glu.end());
        return min({cal2d(g), cal2d(gl), cal2d(gu), cal2d(glu)});
    }
};
